-- ----------------------------
-- Table structure for t_artical
-- ----------------------------
DROP TABLE IF EXISTS `t_artical`;
CREATE TABLE `t_artical` (
  `articalId` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(40) DEFAULT NULL,
  `content` text,
  `publishDate` datetime DEFAULT NULL,
  `author` varchar(20) DEFAULT NULL,
  `typeId` int(11) DEFAULT NULL,
  `click` int(11) NOT NULL DEFAULT '0',
  `isHead` tinyint(4) DEFAULT NULL,
  `isImage` tinyint(4) DEFAULT NULL,
  `imageName` varchar(20) DEFAULT NULL,
  `isHot` tinyint(4) DEFAULT NULL,
  PRIMARY KEY (`articalId`),
  KEY `FK_t_artical` (`typeId`),
  CONSTRAINT `FK_t_artical` FOREIGN KEY (`typeId`) REFERENCES `t_articaltype` (`articalTypeId`)
) ENGINE=InnoDB AUTO_INCREMENT=67 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of t_artical
-- ----------------------------
INSERT INTO `t_artical` VALUES ('58', '몽당치마', '<p><span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp; &nbsp;너울 쓰고 리씨가문의 문턱을 넘어서는 그날까지도 나는 그 녀인을 모르고있었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;가계가 량반의 후예라서 그랬던지 잔치날 시집에 모인 친척들은 많기도 했다. 그속에서도 나는 그 녀인을 본적이 없었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;잔치날 끊임없이 모여드는 시가편 친척들을 보면서 나는 마음을 가라앉힐수 없었다. 본가집살림이 궁하다보니 친척들에게 올릴 례단을 남들처럼 준비못했으니 말이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;집안에 무슨 희사거나 큰일이 생길 때마다 좋소 궂소 하는것도 친척들이요 잘했소 못했소 하는것도 친척이니 말이다. 남들이야 극상해서 잘먹었소 못먹었소 하는 소리로 끝을 보지만 친척들은 입술에 말을 발라가지고 두고두고 옛말을 하게 되니 기실 반가운것도 친척이요 무서운것도 친척인 셈이다. 지어 인사차례마저 내가 받을 절을 네가 먼저 받았다거니 네가 받을걸 내가 먼저 받았다 하며 노염내고 돌아앉게 되니 가딱히면 말썽이요, 데꺽하면 당장 떠난다는판이다. 하물며 한다 하는 리싸가문의 맏며느리로 들어서는 내가 례단을 푼푼히 준비하지 못했으니 뒤가 무겁지 않을수 있겠는가!</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;첫솜씨를 본다며 이튿날아침 진지를 새각시가 짓는것은 조선족이 재래로 내려온 습관이였다. 글눈보다 일눈을 먼저 틔운 나를 놓고보면 그까짓 밥을 짓는것쯤은 대수가 아니였지만 오늘 숱한 친척들 앞에 나서고보니 어쩐지 손이 말을 듣지 않았다. 한두사람의 밥도 아니요, 수십명의 밥을 지어야 하니 어떻게 하면 타지도 안고 설지도 않게 눅지도 않고 되지도 않게 하겠는가. 예전부터 사람들은 처음 짓는 밥이 눅으면 가시복을 받지 못하며 설면 살림이 궁해지고 타면 집안이 망한다고 하지 않았는가! 하기야 밥이 타도 여차여차 하게 복을 누리고 눅어도 여차여차하게 자손들을 잘 키운다면서 아침을 쓰기 마련이지만 같은 값이면 분홍치마라고 어쨌든 첫밥을 물맞게 잘 지어야 했다. 아마 이래서 삼일전까지 색씨는 기쁨절반 시름절반이라고들 하는 모양이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아유, 새식씨가 벌써 가마목에 앉았구만.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;내가 밥물을 맞추느라고 가마에서 물을 떳다넣었다 하는데 키가 훤칠한 40좌우의 녀인이 정지문을 떼고 들어와 가마안을 들여다보더니 《묵은 쌀과 달라 햇쌀이 돼서 물을 좀 적게 타오!》라고 조용히 귀띔해주고는 부엌으로 내려앉는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 밥물을 떠내며 그 녀인을 내려다보았다. 낯선분이였다. 약혼해서 2년사이에 나는 남편과 함께 친척집을 다 돌아보았고 시집에서 약혼잔치를 차렸을 때도 사돈의 팔촌까지 다 모여왔지만 지금 부엌에 앉아있는 이 녀인만은 본적이 없었다. 장작을 지피던 그 녀인은 안심되지 않았던지 우쭐 일어나 밥물을 보고서야 뺨으로 흘러내린 머리카락을 귀밑으로 쓸어올리며 되앉는것이였다. 그 녀인이 일어서는 순간 나는 어덴가 격에 맞지 않는 그의 옷매무시에 눈을 주었다. 몽당치마였다. 무릎을 겨우 가리운 그 검은 몽당치마는 색이 날대로 날았다. 아마 부엌일을 하려고 뉘것이건 쥐우는대로 대충 걸치고 왔나보다 하고 나는 생각했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이 사람 조카댁, 왔구만.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;밖에 나갔던 시어머님이 그 녀인을 보자 희색이 만면해지면서 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《조카댁?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 흠칫 놀랐다. 어머님의 조카댁이면 나에게는 동서간이 되지 않는가? 그런데 왜 나는 여직 모르고있었을가?</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《그 입술에 물집이 진걸 보게. 사촌시동생을 장가보내려다 자네가 드러눕고 말겠네.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님이 혀를 끌끌 차면서 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《작은어머님도 별말씀 다 하세요. 그만한 일을 가지고 뭘!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《자네 몸은 쇠로 빚었나?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《되려 부끄러워요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;(알고보니 사촌형님이였구나!) 나는 인사도 못 올리고 처음 대하는 사촌형님을 보기 면구스러워서 귀밑이 화끈해났다. 례의 바르고 인사범절이 여간하지 않다던 리씨네 가문에서 왜 사촌끼리도 인사를 시키지 않았담?</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;그 형님도 낯이 빨개진채 부지깽이로 불만 뚜지고있었다. 나의 눈길은 다시 그 몽당치마에 돌려졌다. 서면 겨우나마 무릎마디를 가리워주던것이 쭈크리고 앉으니 무릎도 가리우지 못했다. 그밑으로는 수갑이며 양말짝을 풀어서 뜬것 같은 알락달락한 내의가 보였다. 그것은 나이와는 전혀 어울리지 않아 나에게 깊은 인상을 남져주었다. 아침후 내가 제일 걱정하는 가문잔치가 벌어지게 되였다. 매 한사람에게 드릴 례단을 한가지씩 상우에 놓고 신랑이 술을 부으면 신부가 술잔을 권하다. 그다음 신랑신부가 함께 절을 올리는것이니 아마 집안사람끼리 하는 잔치라고 하여 가문잔치라 하는가싶다. 나는 옆에서 시키는대로 시아버님부터 차례로 내려가면서 례단을 올려드리고 술을 권한 다음 남편과 함께 절을 하였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;그럭저럭 가문잔치도 거진 끝나게 되였다. 하지만 당당한 자리를 차지하고 앉아서 새각시의 례단을 받아야 할 《몽당치마》의 몫은 그때까지도 없었다. 례의를 놓고보거나 친척들의 차례를 놓고보아도 사촌형님은 진작 례단을 받았어야 했다. 헌데 누구도 사촌형님을 찾는 사람이 없었다. 나는 너무도 이상하여 옆에 계시는 시어머님을 보고 가만히 귀띔했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《어너님, 사촌형님은요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아유, 깜박 잊었구만.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;그제야 시어머님은 가마목께로 눈길을 던지며 소리쳤다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이 사람 조카댁! 이 사람―》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;가마목은 비여있었다. 가문잔치가 벌어지기전까지도 땀벌창이 되여 가마를 안고 돌던 사촌형님은 어데 갔는지 보이지 않았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《빨리 동불사댁을 찾아오게!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님이 정지에 대고 소리쳤다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《빈손으로 왔으니 자리를 피했나봐요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;같은 사촌동서인 조양천댁이 입을 비쭉거리며 찾을 필요없다는듯이 말했다. 나는 어쩐지 그 소리에 시큰한 그 무엇을 느꼈다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;인사차례가 끝난 뒤끝엔 부어라 머셔라 하는것이 격으로 됐으니 또 술상을 벌려야 했다. 아주머니들도 많고 쌍태머리를 드리운 처녀들도 많았지만 선뜻 팔을 걷어올리고 가마목을 차지하는 녀성은 하나도 없었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이 사람 조카댁, 빨리 상을 차리게!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;뒤끝이 부산하고 그릇소리가 들리지 않으니 시어머님이 조양천택을 보고 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《동불사형님은 어데 가셨나? 인남아, 빨리 가서 동불사맏어머님을 오시라고 해라!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천댁은 어느새 쥐였는지 닭고기를 아들에게 주며 소리쳤다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니 동불사댁만 맛인가? 자네가 가마목에 앉으면 못쓰나?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님이 안색을 흐리우며 말씀했다. 손포가 많지만 진짝 손쓸 사람이 없었으니 말이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《동불사형님이 다 해놔서 뭔지 알아야 하지요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;산뜻하게 차려입은 조양천형님은 좀체로 가마목을 차지하러 하지 않았다.&nbsp;</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《칠칠한 녀자들이 한구들이 돼도 가마목 지킬 사람은 하나도 없구나. 쯧쯧&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님이 조카들과 손녀들을 둘러보며 나무람했다. 그래서야 정지에 앉았던 녀자들이 뜨직뜨직 가마목께로 다가갔다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;바로 이때 문소리와 함께 《몽당치마》가 정지문안에 들어섰다. 그제야 정지에서는 오래간만에 만나는 친지를 대하는것처럼 일어서면서 웃음꽃들을 피우며 야단법석이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니 어데 갔다가 인제 오우?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《작은어머님이 례단을 놓고 얼마나 기다렸다구!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님이 없으니 한자리 빈것 같습데.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;그 누구보다 더 기다렸다는듯이 뽑아대는 오양천댁의 가늘면서도 삐여진 쏘프라노는 더더욱 도드라졌다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;《속탈을 만나서&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;귀맛돋구는 그 《바스음성》이였다. 그 소리는 고음으로 고아대던 다른 음들과 대조되면서 다감하게 들려왔다. 하건만 그 소리는 햇쌀이 돼서 물을 덜 탄다고 소근거리던 그런 부드러운 저음과는 달리 어덴가 잦아드는감이 났다. 그는 실주름이 얼기설기 잡혀 무척 인자해보이는 얼굴에 서글픈 미소를 지으며 파을 걷고 가마목에 앉는것이였다. 어렸을적부터 굳은일 마른일 가리지 않고 일에서 뼈마디를 키워왔는지라 나는 그의 숙련된 일솜씨에 마음이 확 쏠렸다. 무슨 일에서나 입보다 손발을 먼저 놀리는 그런 류형의 농촌녀성이였다. 잔치객들이 잔치선물로 떡이며 과자 등속을 이고 들고 한사람 두사람 떠나가기 시작했다. 하건만 동불사형님은 가마목을 척 차지하고 앉아서는 그 시끄러운 뒤거둠질을 도맡아 하는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님, 리씨가문에 친척이 많다더니 정말 많기도 하구만요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;인사할 때는 다 소개받았으니 몇몇 가까운 친척을 내놓고는 딱히 몇촌벌되는 관계인지 까맣게 잊고있던 나는 그 널직한 팔간집에 꽉 들어찼던 친척들을 상기하면서 동불사형님보고 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《많다면 많고 적다면 적소!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《그 많은 친척들을 두고 적다니요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《한집안끼리도 친척이 있는 집이 있고 없는 점이 있소.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;알고도 모를 소리쳤다. 나는 의아한 눈길로 형님을 쳐다보았다. 하지만 팥죽같은 땀을 흘리며 일하는 형님의 낯에서는 아무것도 읽을수 없었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《왜 아주버님과 같이 오시지 않았어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아주버님?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;형님은 의혹에 찬 눈길로 나를 쳐다보다가는 계속 일손을 다그치는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《그럼요. 애들까지도 다 데리고 오지요. 친척마다 다 데리고 왔던데요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《대접도 받지 못한 애들을 데리고 와선 뭘 하겠소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《네?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;이쩐지 동불사형님은 입을 꾹 다물며 일어서시더니 구지렁물을 버린다, 쓰다남은 채소를 사랑채로 내간다 하는 선일들만 골라하는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 뒤일을 말끔하게 해놓고는 저녁차로 떠나겠다고 서둘렀다. 시어머니는 내가 놓았던 례단이며 먹다남은 떡이며 낡은 옷견지같은것들을 한보따리 꾸려서 동불사형님에게 주었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《헌투레기뿐인데 애들에게 기워 입히게나!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《작은어머니, 나도 친척이느라고 나설 때가 있을가요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 말없이 보꾸레미를 받으며 눈물을 훔쳤다. 나는 가슴이 찡해남을 어쩔수 없었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《애들이 자라는것이 잠시라는 말도 있지 않소? 그것들이 자라면 옛말할 때가 있을거요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머니는 자케트호주머니에서 돈 5원을 꺼내여 형님의 손에 쥐여주었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《적은데 애들 학비에 보태오!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이 꾸레미는 받아도 돈은 못받겠어요. 빈손으로 온것만 해도 낯을 들지 못겠는데&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시리시리한 남성적인 체대에 맞는 《바스음성》이 그의 목구멍에게 새여나왔다. 아, 나는 그제야 동불사형님이 왜서 례단을 올릴 때 자리를 피했는가를 알았다. 그리고 리씨가문을 나들기 시작한지 2년남짓이 되지만 왜 지금껏 모르고있었는가를 알았다. 나는 물갈기이는 가슴을 억누르며 사촌형님의 손을 꼭 쥐고 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님, 치마라도 한감 사입으세요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이렇게 신세만 져서&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 밤차로 떠나갔다. 하건만 눈에 익은 그 몽당치마와 귀맛돋구는 그 바스음성만은 잊을수 없었다.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">2</span><br />\r\n<br />\r\n<br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시아버님이 교육처 처장으로 사업하고 남편이 공업국 부국장으로 일하는 덕분이였던지, 아니면 손이 두텁게 부조를 해서 그랬던지 나는 리싸가문의 장손댁도 아니건만 간곳마다 장손댁으로 받들렸다. 촌수를 캐여보면 리씨가문의 장손댁은 동불사형님이였고 진짜 후에는 소 깔먹듯 무르고센것없이 밥소래를 굽내며 무럭무럭 자라는 그 집 자식들이였다. 추석이요, 설이요, 보름이요 하는 명절때마다 집안사람들이 이집저집 몰켜다니며 닭을 잡는다 돼지를 잡는다 하지만 그때마다 《몽다치마》는 그림자도 보이지 않았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니, 동불사형님은 왜 오시지 않았어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시집온 그해 설쇠러 조양천형님네 집으로 갔을 때 나는 돼지를 엎질러놓고 순대를 만들어 자시는 친척들을 둘러보며 물었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《그 형님이사잔밥들때문에 문을 나설수 있어야지?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천형님은 대수롭지 않게 대답했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《홀몸으로 집도 돌볼라니 고급사일도 나가 할라니 발 뗄 새나 있겠소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나의 물음에 이 사람 저 사람들이 동불사형님을두고 하는 말이였다. 나는 그소리가 못내 섭섭했다. 그러나 그저 섭섭할뿐이였다. 내가 《바스음성》을 생각했다면 고작 불쌍하구나 하는것에 그쳤을것이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 이렇게 먹어라 써라 할 때마다 자리를 피하군 했다. 하지만 일손이 모자라거나 고된 일이 생길 때면 저마다 그를 찾았고 그를 생각했으면 그를 데려오려고 사람까지 띄우군 했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;내가 시집온 이듬해가을에 시아버님이 회갑잔치를 하게 되였다. 조양천형님은 회갑잔치를 하게 되였다. 조양천형님은 회갑전날 아침차를 타고 왔다. 그는 보라는듯이 과자와 사탕, 과일을 넣은 큰상차림구럭을 이고 들고 문에 들어섰다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아유, 오늘은 차객들이 어찌나 많은지 겨우 비비고 올라앉았어요. 짐아라는게 작히나 무거운가!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천댁은 남들이 듣지 못할가봐 길게 말을 뽑았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이 사람 조카댁, 길에서 고생했네.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님이 얼른 일어나 짐받으러 부엌으로 내려서면서 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《조양천에 색과자가 없어서 룡정까지 갔다오느라 늦었어요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천댁은 똬리를 만들었던 수건을 풀어 땀을 씻는척하며 계속 떴다고았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《상을 차리지 않으면 못올덴가? 원―》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《조카로서 작은아버님의 회갑상도 차리지 않겠어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;친척들은 이구동성으로 조양천댁의 처사가 이만저만이 아니라고 칭찬들을 늘여놓았다. 나는 조양천형님의 성품이 워낙 그렇다싶어 맞장구를 치지 않았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이 사람, 일이 처져서 말이 아니네. 빨리 옷을 갈아입고 가마목에 앉게!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님이 일재촉을 했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니 동불사형님은 오지 않았어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천형님은 그제야 고방에서 머리를 내밀고 누구인가를 찾듯이 이곳저곳을 살피며 물었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《입이 넷이 그 손을 믿고사는 사람을 어찌 오라가라 하겠나? 대약진이라 밤낮 눈코뜰새없이 도는 떼에 말이네.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님이 딱한 표정을 지으며 동불사형님을 감싸나섰다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《제아무리 목구멍이 포도청이래도 시아버님 맞잡인 작은아버님의 회갑에도 오지 않다니요? 아무리 그렇다고 이 좋은 세월에 산 사람의 입에 거미줄이 쓸겠어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;남을 흠잡으면 그만큼 제 어깨가 올라가는지 오양천댁은 점점 더 약을 올렸다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;이때다. 덜컹하는 부엌문소리와 함께 동불사형님이 들어섰다. 일년전에 입고 왔던 그 매무새 그대로였다. 딴 차림새라면 우둘진 체대에 어울리지 않게 젖가슴이 팽팽할 정도로 단추를 겨우 채운 학생복저고리를 입은것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《동불사형님이 오셨어요!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 너무나 기뻐서 일어서며 소리쳤다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이 사람, 우정 알리지도 않았는데 어떻게 알고 찾아왔나?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시어머님은 자리를 털고 일어서면서 동불사형님이 쥐고온 술병을 받았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《선가을을 끝내고 오느라고 그만 늦었어요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;오래간만에 들어보는 남성적인 그 《바스음성》이였다. 사람이 직접 눈앞에 나타나자 방금까지 이러쿵저러쿵하면서 콩이야 팥이야 하던 친척들은 여간한 성의가 아니라고 칭찬이 자자했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《나도 방금 왔는데 형님은 무슨 차를 타고 오셨소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천댁이 토라져서 자리에서 일어나지도 않고 물었다. 그러면서 그는 시어머니의 손에 쥐여있는 술병에 야멸찬 눈길을 주었다. 그 눈빛은 차라리 빈손으로 올게지 술 한근이 다 뭐요? 하고 말하는상싶었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《뻐스를 타고 왔소.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 고방으로 들어가더니만 저고리만 벗어 벽에 걸어놓고 맨적삼바람으로 나오면서 끈으로 허리를 질끈 동이고는 가마목에 앉는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;이번 시아버님의 회갑에 친척들한테서 들어온 상만 해도 스물네상이나 되였다. 회갑상 상수를 보고 그 집안을 알고 집집이 차린 상으로 집집을 안다는 말이 이를 두고 하는 말이리라. 그래서 그런지 친척들마다 제 상차림에서 이목을 끌게 하느라고 상우에 놓았던 색과자를 이쪽저쪽 옮기면서 상을 안고 법석거렸다. 그런가 하면 상차림가지수를 헤느라 여념없는 사람도 있었다. 이제 그 가지수를 가지고 뉘집은 이렇고 뉘집은 저렇다 하면서 말에 장부를 앉힐판이다. 상차림에서 조양천동서의 승벽은 여간하지 않았다. 남이 차린 상에 눈그루를 박다가도 자기 상에 없는것이 있으면 회갑상에 그런걸 다 놓겠는가고 하면서 삐뚜렁소리를 하기가 일쑤였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;남들이 상차림에 분주할 때 동불사형님은 머리를 푹 떨구고 손님접대할 음식을 만들고있었다. 안절부절해서 일손을 바로 놀리지 못하며 몽당치마로 무릎을 가리우느라 치마폭을 당기는 그 행동거지에서 나는 그의 기분상태가 몹시 가라앉음을 직감했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시아버님과 시어머님께 올리는 인사가 허리를 넘어섰는데도 지난번 잔치때처럼 동불사형님의 이름은 없었다. 그 형님보다 내가 더 섭섭했다. 나는 부엌에서 매운탕을 끓이고있는 형님을 내려다보았다. 형님도 나를 쳐다보았다. 찰나, 나는 형님의 눈굽에 눈물이 괴여있음을 보았다. 형님은 나를 보더니 인츰 일어서서 고방으로 들어가는것이였다. 작년처럼 자리를 피하는것이라고 나는 생각했다. 하지만 몇분 지나지 않아 나의 생각이 틀렸다는것이 증명되였다. 형님이 어제 입고 왔던 그 학생복을 단정히 입고 술병을 들고 고방에서 나왔으니 말이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님, 어머님께서 형님의 상을 마련했으니 들고 나가세요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;기분을 잡치지 않고 인사올리려 나가는 동서의 그 인품이 너무나 고맙고 기꺼워서 나는 시어머님이 차려놓은 상을 가리키며 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《겉치례를 새서 뭘 하겠소? 있으면 있는것만큼 행세해야지.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 동동 달리는 몽당치마를 내리당겨놓고는 코신을 발에 걸고 문을 나서는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《친척에서 더 인사할 사람이 없소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;팔촌시형이 상례집행을 서다가 주위에 꽉 박아선 사람들을 둘러보았다. 이때, 몽당치마를 입은 동불사형님이 큰상이 아니라 술병을 들고 시아버님의 앞으로 가더니니 술을 부어올리는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《작은아버님, 작은어머님, 오래오래 복하게 앉으세요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 살풋이 앉으며 절을 올렸다. 작년에 입고 왔던 그 알락달락한 아래내의에 헝겊으로 기운자리가 몽당치마에 가리우지 못하고 드러났지만 절하는 맵시가 하도 탐탁하고 진지하여 그의 깨끗한 마음을 흐리우지는 못하였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《야― 질부가 주는 술이 더 달구나!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시아버님은 다른 사람들의 부은 술은 입술이나 댔다말았지만 동불사형님이 부은 술은 한방울도 남기지 않고 굽을 냈다. 그 소리와 함께 조양천댁이 입을 삐죽하는것이 나의 눈결에 피뜩 띄였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동북사형님은 이번에도 다른 손님들까지 다 떠나고 없었지만 마지막 뒤거둠질까지 싹하고 저녁늦차로 떠났다. 나는 내가 일할 때 입으려고 준비했던 검은 속치마와 밤색 저고리를 동서에게 드렸다. 우리는 체대가 엇비슷하여 그가 나의 옷을 입는다 해도 남의 옷을 입은것 같지는 않았던것이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이렇게 신세만 져서&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;역시 전번과 같은 말을 남기며 그는 차에 올랐다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;인생의 행로는 파란곡절로 얽혔있다더니 말그른데 없다. 환갑이 지났건만 50대의분들을 찜쪄먹는다던 시아버님이 글쎄 중풍을 만나 급작스레 세상뜰줄이야 누가 생각인들 했으랴! 가문의 기둥으로, 집안의 대들보라고 여겨오던 시아버님이 세상뜨자 단란하던 우리 집은 차츰 네귀가 비뚤어지기 시작했다. 시아버님이 세상뜬지 몇달 되지도 않았는데 시름시름 앓던 시어머님이 위암이라는 진단을 받고 시아버님의 뒤를 따라 가셨다. 이렇게 되여 한해 몇달사이를 두고 나는 시부모를 몽땅 여의게 되였다. 엎친데덮친다고 그 이듬핸 공업국 부국장으로 일하던 남편이 《민족공업을 발전시켜야 한다》는 기발을 들고 협애한 민족주의 사상을 고취했다는 감투끈올 민족주의분자에 우경기회주의분자로 되는통에 우리 집은 령락의 길에 들어서게 되였으며 얼마 되지 않아 생활의 밑바닥에서 허덕이게 되였다. 시부모가 세상뜨자 뜸해지기 시작하던 친척들의 발걸음이 남편이 그 무슨 분자로 되자 영 자취를 감추고말았다. 남편이 로동개조를 하게 되자 나는 그를 따라 옥천동으로 내려오게 되였다. 이때 나는 이미 두 남매를 낳은데다 또 태기가 있게 되여 오래지 않아 자식 셋을 가진 어머니로 되게 되였다. 남편이 벌지 못하는 대신 내가 벌어야 하겠는데 배가 불룩하여 일도 못하게 되자 우리 집 생활은 궁하다못해 쌀독에 거미줄이 칠 지경으로 되였다. 돈이 돈을 낳고 가난이 가난을 낳는다고 가난할수록 애들마저 밥을 어떻게나 먹어대는지 남들과 똑같이 량식분배를 받아도 우리 집은 항상 딸리기만했다. 나는 할수 없이 시내에 있는 친척들을 찾아가서 가을에 입쌀을 주기로 하고 강냉이쌀과 강냉이가루를 꿔다먹고는 벼를 찧기 바쁘게 이여다주군 했다. 이렇게 일년에 몇백근씩 꿔다먹은 쌀을 물어주고나면 그 이듬해봄을 넘기기가 바빴다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;계급투쟁을 고리로 하는 그 삼엄한 시기에도 발길을 끊지 않은것은 유독 《몽당치마》인 동불사형님이였다. 남편이 로동개조를 하게 될 때도 일부러 찾아와서 나를 위로해줬고 옥천동으로 내려올 때도 와서 집까지 꾸려주었었다. 지금은 그전보다 몇배나 더 먼거리를 상거해있고 자동차까지 바로 통하지 않는 벽촌에 내려와 있건만 감자가 나지면 감자를 이고 강냉이가 나지면 강냉이를 이고 찾아오군 했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp; &nbsp; 래일은 팔월 한가위날이다. 남편은 부모들의 산소를 벌초하러 떠나가고 집에는 철부지들과 나뿐이였다. 여느 집같으면 떡친다 막걸리를 거른다 하며 야단치련만 우리 집은 초상난 집처럼 스산하기만했다. 전같으면 친척들이 찾아들어 씨암탉을 비튼다 돼지를 잡는다 하며 법석이련만 지금은 아무도 찾아오는이가 없다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《한집안끼리도 친척이 있는 집이 있고 없는 집이 있소.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 그때 동불사형님이 하던 말뜻을 오늘에야 딱히 알게 되였다. 지금 우리 집은 적적해도 친척들 뉘집에선가는 떡치고 술빚고 부어라 마셔라 하면서 웃음꽃을 피울것이다. 나는 그들이 그립기도 하면서 한없이 미웠다. 남남간도 아닌데 아무려면 그렇게도 무정할수 있단말인가?</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《소쩍―소쩍―》 뒤동산 소나무숲속에서 소쩍새가 술피 운다. 그 처량한 울음소리는 외롭고 쓸쓸한 가슴에 비애의 파문을 일으키면서 가슴속까지 파고들었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니 이 집에서는 왜 상기도 불을 켜지 않고있나?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《바스음성》이였다. 항상 귀에 쟁쟁 울리던 그 웅글은 목소리였다. 나는 자리에서 벌컥 일어섰다. 내가 미처 문을 열어드리기 바쁘게 《삐꺽―》하는 부엌문소리와 함께 동불사형님이 큼직한 광주리를 이고 들어서는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님, 흑흑&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 형님을 보자 친어머니를 만난것처럼 이때까지 쌓이고 쌓였던 설음이 북받쳐올라 그의 가슴에 얼굴을 묻고 흐느끼기 시작했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니 집에 불상사라도 생겼소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;근심어린 《바스음성》이 우렁우렁 나의 가슴에까지 마쳐왔다. 하지만 나는 무턱대로 울기만했다. 그저 울고만싶었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니 대보름에 웬 곡성이요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;선뜩하고 정수리에 찬 물기가 떨어졌다. 식은 땀방울이였다. 그제야 나는 그의 가슴에서 머리를 들었다. 동불사형님은 그때까지도 광주리를 인채 철철 땀만 흘리고있었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니, 내 원&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 눈물을 훔칠념도 없이 짐을 받아내려놓고 인츰 석유등잔에 불을 달았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《애 아버지는 어데 갔소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;근심스런 물음이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《벌초하러 갔어요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《오, 난 또&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 그제야 신을 벗고 구들로 올라서면서 시름놓은듯 후―하고 한숨을 쉬였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《눈물을 흘리기 시작하면 매일 동이로 쏟아도 부족하요. 눈물을 삼키며 살아야 하오. 특히 녀자들말이요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;그는 머리수건으로 나의 눈물을 닦아주며 말했다. 형님의 따스한 손길에 눈물은 밑모르는 샘처럼 솟기만했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《추석쇠러 왔는데 웃어야 할 대신 울기부터 하다니 원―》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《왜 이렇게 늦었어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 눈물을 훔치며 그제야 인사말을 했다.</span></p>\r\n\r\n<p><span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp; &nbsp; 《래일 추석이라 객들이 어찌나 많은지 겨우 막차를 잡아탔소.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 불을 때려고 부엌에 내려섰다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《내가 땔테니 올라가오. 그 몸으로 오르내리기가 얼마나 힘들겠소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 나를 떠밀어올리고 자기가 부엌에 내려앉으며 말했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;가마는 굶을 때가 있어도 부엌아궁이는 굶은적이 없는 도목나무고장이라 장작에 불을 지피니 가마의 물이 인츰 끓어올랐다. 형님이 오니 추석기분이 도는것만 같았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 쌀을 씻어 안치며 형님을 내려다 보았다. 부엌아궁이에서 비껴나오는 불빛에 비낀 형님의 모습은 때국물이 흐르던 그전 궁상이 아니였다. 몸에 걸친것이 몽당치마도 아니요 알락달락한 내의도 아니요 학생복저고리도 아니였으며 내가 드렸던 그 치마저고리도 아니였다. 그것은 새로 지어입은 회색 혼방직치마저고리였다. 가난때를 벗는것이 제일 큰 떼벗이라더니 가난티를 말끔히 가셔버리지는 못했지만 큰애가 학교문을 나서서 벌고 돈냥이나 손에 쥐게 되니 탈피를 한것처럼 말끔했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님, 형님은 이젠 옛말을 하게 됐어요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《동서는 오랜줄 아오? 이제 생원의 일이 풀리고 애들이 자라면 나보다 더 옛말을 하며 살게요. 세월이 류수라 오랜것 같으면서도 빠른것이 세월이요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님, 그 많던 친척들이 다 어데 갔을가요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 내가 시집올 때와 시아버님의 회갑날을 상기하면서 이렇게 물었다.&nbsp;</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《먹어라 써라 하는것이 친척이요, 발길을 끊으면 이웃보다 못한것이 친척이라지 않소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 가마가 끓는것을 보고 장작 몇가지를 집어넣더니만 구들로 올라왔다. 그는 자는 애들을 유심히 내려다보다가 광주리를 헤치기 시작했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《초바심을 해서 친 차이떡이요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;형님은 들기도 힘겨운 큼직한 떡보자기를 내놓았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이건 햇쌀이구 이건 소고기구 이건&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;형님은 나이론수건에 싼 천을 헤쳐보이면서 말을 이었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《동서에게 주려구 끊었는데 마음에 들겠는지 모르겠소.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;하얀 나이론적삼과 밤색 치마감이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《지금 이것저것 가릴 신세가 됐나요? 조카들이나 사입히지 돈을 팔며 왜 사왔어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;시집올 때의 밑천을 다 부려먹고 시집에서 해준것마저 거덜이 나다나니 나에게는 지금 입고있는 누덕누덕 기운 단벌치마저고리밖에 없었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《없고보면 모든것이 구멍빠진 항아리와 같소. 곁에서 도와주는것이 몇참 간다고 그러우? 제 손으로 일어서야지. 남이 돕는건 그때뿐이요. 신세를 진다는 소리만 들었지&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《떨어진다 하니 어째 이렇게 칼로 자르듯 몽딱 떨어져나가는지?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《그러게 굽빠진 항아리는 때도 가난 구멍은 때지 못한다고 하지 않소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《밤낮 운동운동하면서 들볶아대니 지금 같아서는 헤여나갈것 같지 못해요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《제대로 땅파먹고 살라는 때가 있겠지. 밤낮 어렇겠소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;저녁을 치르고 우리는 자리에 누웠다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;길에서 지쳤던지 시름을 덜어서 그랬던지 동불사형님은 눕자마자 코를 골았다. 하지만 나는 오만가지 생각에 좀체로 잠들수가 없었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《소쩍―소쩍―》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;소쩍새가 다시 울기 시작했다. 하건만 나는 외롭지 않았다. 그것은 옛날의 《몽당치마》 《바스음성》이 나의 옆을 지키고있기때문이였으리라!</span><br />\r\n<br />\r\n<br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">3</span><br />\r\n<br />\r\n<br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;남편만 아니였어도 나는 친척들과의 거래를 끊어버렸을것이다. 불쌍한 남편의 면목을 봐서 나는 친척들과 다시 래왕하지 않으면 안되였다. 하기는 내뒤가 끌려서 쌀꾸러 다니면서부터 래왕이 시작되기는 했지만 말이다. 여름한철 쌀꾸러 다니고 가을한철 갚느라고 치마꼬리가 닳게 다녔으니 제 발로 문을 연셈이다. 하지만 나는 잔치같은 회사에만은 눈을 감아버리려 했다. 내밀것이 있어야 떳떳이 나서련만 쥐면 두주먹밖에 없는 무우밑둥같은 신세에 그것도 차리고 나설 옷마저 없는 주제에 어데로 나다닌단 말인가? 하지만 친척들은 무슨 희사가 생길적마다 나를 잊지 않았다. 그전에 백도라지요, 노들강변이요 하면서 춤판을 벌릴 때는 몽당치마를 잊었어도 일손이 딸릴 때마다 몽당치마를 생각하듯이 가마목손이 부족할 때마다 나를 생각하군 했으리라.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;한번은 조양천형님이 아들을 보내서 맏딸이 시집가니 오라는 청탁을 하였다. 남편이 로동개조를 하기 시작해서부터 십몇년동안 발길을 끊었던 조양천형님이 사람까지 띄워 나를 청했으니 심히 놀라올 일이 아닐수 없었다. 어떤 때는 문턱이 닳게 다니다가도 어떤 때는 모른척하고 나앉은 일을 생각하면 분하기 그지없었으나 조카들을 봐서라도 가야 했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아유 동서, 제앞이 막막해서 살자고 버둑거리다니 한번 가보지도 못하고&hellip;어찌 고생하오? 생원은 무사하오? 애들은 잘 자라오?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;내가 문을 떼고 들어서자 조양천동서 될 말을 단꺼번에 뿜어버렸다. 십몇년새에 이마의 자주름이 늘어났지만 남달리 삐여진 그의 고움은 여전하였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《고생이야 다 같지요. 그간 무사하셨어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《우리는 그럭저럭 탈없이 보내오. 아유, 그 곱던 낯이 왜 이렇게 됐소?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;달망지게 생긴 조양천형님은 나의 손목을 잡고 손등을 싹싹 어루만져주면서 있는 걱정 없는 걱정 다했다. 환갑때에 헴이 든다더니 쉰고개를 바라보는 조양천형님 인제야 지난날을 후회하는것 같다 여겨 얼어붙었던 나의 가슴속 한귀퉁이가 스르르 녹아내린는것만 같았다.&nbsp;</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아니 잔치라면서 왜 집안이 이리도 조용한가요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 잔치객들이 없는 조용한 집안을 둘러보며 물었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《잔치날자는 아직 열흘이 있소.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《네?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《제 에미가 무재간이 돼서 그런지 딸년도 무재간이요. 잔치날자는 바득바득 다가오는데 꽃방석이며 베개머리에 수를 하나도 놓지 못했소. 그래 생각다못해 동서를 불렀소.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나에게는 어머님손끝에서 물려받은 한가지 잔재간이 있었다. 수놓이였다. 이 몇년래 나는 저녁마다 수놓이를 해서 장간새를 사고 애들 학비에 보탰던것이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 그날 저녁부터 재봉기앞에 앉아서 꽃방석이며 홰보, 베개머리에 수를 놓기 시작했다. 곷방석이나 베개머리에 놓은 수를 보고 첫날색시의 손끝을 가늠하기 마련이니 나는 시집가는 조카를 대신해서 있는 정성과 재간을 다 피웠다. 휘늘어진 수양버들에 제비가 날아드는것도, 활짝 핀 란초꽃에 나비 쌍쌍 날아예는것도, 소나무옆을 가도는 벽계수에 백학이 내려앉는것도, 달밤 송백수를 품에 안은 츠렁바위에서 호랑이 따웅하며 앞발을 쳐드는것도 어쨌든 이때까지 내가 보아왔고 좋다고 생각되는것을 몽땅 수놓아주었다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;내가 밤낮 여드레를 제봉기앞에서 바삐 보내고 물러앉은 그날 동불사형님이 애들까지 몽땅 데리고 들어섰다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아유―작은동서가 한발 앞섰구만.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《형님!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;나는 어찌나 반가왔던지 한참동안이나 동불사형님의 손을 꼭 쥐고 놓지 않았다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《대학에 간 큰녀석은 공부를 잘하오?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《네, 형님집에서도 바쁠 땐데 또 돈을 보냈더군요. 며칠전에 그 애한테서 편지가 왔댔어요.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;아버지때문에 대학교로 추천을 받지 못하고 호미대학을 다니며 밥을 한소랭이씩 굽내던 우리 집 큰녀석이 금년봄에 시험을 쳐서 대학에 붙었댔다. 대학에 붙은건 좋은 일이나 뒤를 댈 태산같은 걱정으로 그때 눈 한번 제대로 붙여보지 못하고있는데 동불사형님이 데트론옷 한벌과 돈 이십원을 갖고 찾아왔던것이다. 그런데 며칠전 큰 어머니께서 또 돈 이십원을 보내왔다는 편지가 대학에 간 녀석한테서 왔으니 그렇게 고마울변이 또 어데 있으랴!</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《우리 같은것도 잘살라고 하는 이 좋은 세월에 마음껏 공부를 하라고 그러지.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;동불사형님은 이렇게 말하며 구들에 올라섰다. 나는 습관적으로 그의 치마에 눈이 미쳤다. 축 내리드리운 엷은 세루포 만든 회색치마였다. 구들에 발을 올려놓는 그 서슬에 나이론양말목에 감싸인 남색털실 내의가 치미꼬리사이로 보였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《몽당치마와 새루치마&hellip;》</span></p>\r\n\r\n<p><span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp; 나는 몽당치마를 입고 땀을 뻘뻘 흘리며 일하던 동불사형님을 생각하며 조카들을 둘러보았다. 세루가 아니면 라사천으로 몸을 감은 다 큰 쳐녀총각들이였다. 입던 옷견지와 쌀을 이고 내가 동불사로 갔을적마다 때국물에 얼룩진 낯에 꾀죄죄 코를 흘리며 게눈감추듯 밥소랭이밑굽을 내고 나의 밥그릇을 처다보던 그 철부지들이 오늘은 이처럼 의젓하게 번졌는가!</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;어느새 옷을 갈아입었는지 동불사형님은 일할 때 입던 옷을 입고 그전처럼 가마목을 차지하고 앉는것이였다. 그는 이남박을 들다말고 부엌에 앉아있는 나를 내려다보는것이였다. 내가 처음으로 불을 때고있는 《몽당치마》를 내려다볼 때처럼&hellip;나는 치마폭으로 얼른 발목을 덮었다. 목수건이며 양말 할것없이 실이 될수 있는 물건을 다 풀어서 떠입은 누데기 사촌이나 될 얼럭이 진 내의가 드러났기때문이다. 나는 몸둘바를 모르면서 형님을 올려다보았다. 형님은 그때까지도 까딱않고 나를 지켜보고있었다. 나는 그의 눈에서 반디불같은 빛을 보았다. 눈초리에 달려 떨어질가말가 달랑달랑 달려있는 이슬방울이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《동서, 불을 콱 지피우!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;거센 《바스음성》과 함께 동불사형님은 떡살을 힘있게 푹푹 떠서는 이남박에 담는것이였다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《동불사형님, 모두들 례단을 보겠다고 해서 헤쳐놓았는데 형님도 얼른 들어와 보오!》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;역시 짜그르르 구으는 고음이 정지를 향해 흘러나왔다. 하건만 나를 들어와 보라는 소리는 없었다. 섭섭하기는 했지만 차라리 보라고 하는 소리보다 더 반가왔다. 제몫이 없으니 낯을 들이밀고 례단을 구경할 면목이 없으니 말이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이건 작은집에서 보내온 라사천외투고 이건 큰집에서 보내온 세루옷이고 이건&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천형님은 말에 꽃을 피우면서 자랑을 늘어놓았다. 나는 머리를 들수 없었다. 친척마다 면목을 낼수 있는 물건이 다 있었지만 나에게는 아무것도 없었으니 말이다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《아유, 수도 곱게 놓았네. 누가 놓았어요?》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;누군가 이렇게 감탄하는 소리가 떠드는속에서 들여왔다. 나는 속이 꿈틀했다. 그러면서 내 이름이 드러날것이라며 저도 모르게 속이 두근두근했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《이건&hellip;》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;조양천형님의 말이였다. 그 소리는 방금처럼 그렇게 되알지지도 못했고 그렇게 높지도 못했다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《누군 누구겠소. 색시의 손재간이겠지.》</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;누군가 곁에서 쐐기를 쳤다.</span><br />\r\n<span style=\"font-family:tahoma,verdana,stheiti,simsun,sans-serif; font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;《정말 손재간이 이만저만이 아니요. 신랑이 복을 받았다니까.》</span', '2017-08-30 11:42:25', '림원춘', '2', '1', '1', '1', '20170830114225.17', '1');
INSERT INTO `t_artical` VALUES ('59', '우동 한그릇', '<p><span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">해마다 섣달 그믐날이 되면 우동 집으로서는 일년 중 가장 바쁠 때이다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;북해정&quot;도 이 날만은 아침부터 눈코 뜰새 없이 바빴다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">보통 때는 밤 12시쯤이 되어도 거리가 번잡한데 그 날 만큼은 밤이 깊어질수록 집으로 돌아가는 사람들의 발걸음도 빨라지고 10시가 넘자 북해정의 손님도 뜸해졌다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">사람은 좋지만 무뚝뚝한 주인보다 오히려 단골손님으로부터 주인 아줌마라고 불리 우고 있는 그의 아내는 분주했던 하루의 답례로 임시종업원에게 특별상여금 주머니와 선물로 국수를 들려서 막 돌려보낸 참이었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">마지막 손님이 가게를 막 나갔을 때, 슬슬 문 앞의 옥호막(가게이름이 쓰여진 막)을 거둘까 하고 있던 참에, 출입문이 드르륵 하고 힘없이 열리더니 두 명의 아이를 데리고 한 여자가 들어왔다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">6세와 10세 정도의 사내들은 새로 준비한 듯한 트레이닝 차림이었고, 여자는 계절이 지난 체크무늬 반코트를 입고 있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;어서 오세요!&quot; 라고 맞이하는 주인에게, 그 여자는 머뭇머뭇 말했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;저...... 우동...... 일 인분만 주문해도 괜찮을까요......&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">뒤에서는 두 아이들이 걱정스러운 얼굴로 쳐다보고 있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;네...... 네. 자, 이쪽으로.&quot; 난로 곁의 2번 테이블로 안내하면서 여주인은 주방 안을 향해, &quot;우동, 1인분!&quot; 하고 소리친다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">주문을 받은 주인은 잠깐 일행 세 사람에게 눈길을 보내면서, &quot;예!&quot; 하고 대답하고, 삶지 않은 1인분의 우동 한 덩어리와 거기에 반덩어리를 더 넣어 삶는다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">둥근 우동 한 덩어리가 일인분의 양이다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">손님과 아내에게 눈치 채이지 않은 주인의 서비스로 수북한 분량의 우동이 삶아진다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">테이블에 나온 가득 담긴 우동을 가운데 두고, 이마를 맞대고 먹고 있는 세 사람의 이야기 소리가 카운터 있는 곳까지 희미하게 들린다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;맛있네요.&quot; 라는 형의 목소리.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;엄마도 잡수세요.&quot; 하며 한 가닥의 국수를 집어 어머니의 입으로 가져가는 동생.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">이윽고 다 먹자 150엔의 값을 지불하며, &quot;맛있게 먹었습니다.&quot;라고 머리를 숙이고 나가는 세 모자에게 &quot;고맙습니다, 새해엔 복 많이받으세요!&quot; 하고 주인 내외는 목청을 돋워 인사했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">신년을 맞이했던 북해정은 변함없이 바쁜 나날 속에서 한 해를 보내고, 다시 12월 31일을 맞이했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">지난해 이상으로 몹시 바쁜 하루를 끝내고, 10시를 막 넘긴 참이어서 가게를 닫으려고 할 때 드르륵, 하고 문이 열리더니 두 사람의 남자아이를 데리고 한 여자가 들어왔다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">여주인은 그 여자가 입고 있는 체크무늬의 반코트를 보고, 일년 전 섣달 그믐 날의 마지막 그 손님들임을 알아보았다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;저...... 우동...... 일 인분입니다만...... 괜찮을까요?&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;물론입니다. 어서 이 쪽으로 오세요.&quot; 여주인은 작년과 같은 2 번 테이블로 안내하면서, &quot;우동 일 인분!&quot; 하고 커다랗게 소리친다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;네엣! 우동 일인분.&quot; 라고 주인은 대답하면서 막 꺼버린 화덕에 불을 붙인다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;저 여보, 서비스로 3 인분 내줍시다.&quot; 조용히 귀엣말을 하는 여주인에게, &quot;안돼요. 그런 일을 하면 도리어 거북하게 여길 거요.&quot; 라고 말하면서 남편은 둥근 우동 하나 반을 삶는다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;여보, 무뚝뚝한 얼굴을 하고 있어도 좋은 구석이 있구료.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">미소를 머금는 아내에 대해, 변함없이 입을 다물고 삶아진 우동을 그릇에 담는 주인이다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">테이블 위의 한 그릇의 우동을 둘러싼 세 모자의 얘기소리가 카운터 안과 바깥의 두사람에게 들려온다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;으...... 맛있어요......&quot; &quot;올해도 북해정의 우동을 먹게 되네요?&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;내년에도 먹을 수 있으면 좋으련만......&quot; 다 먹고, 150엔을 지불하고 나가는 세 사람의 뒷모습에 주인 내외는, &quot;고맙습니다! 새해 복 많이 받으세요!&quot; 그 날 수십 번 되풀이 했던 인삿말로 전송한다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 다음 해의 섣달 그믐날 밤은 여느 해보다 더욱 장사가 번성하는 중에 맞게 되었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">북해정의 주인과 여주인은 누가 먼저 입을 열지는 않았지만 9시 반이 지날 무렵부터 안절부절 어쩔 줄을 모른다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">10시를 넘긴 참이어서 종업원을 귀가시킨 주인은, 벽에 붙어 있는 메뉴표를 차례차례 뒤집었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">금년 여름에 값을 올려 &#39;우동 200엔&#39;이라고 씌어져 있던 메뉴표가 150엔으로 둔갑하고 있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">2번 테이블 위에는 이미 30분 전부터 &lt;예약석&gt;이란 팻말이 놓여져 있다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">10시 반이 되어, 가게 안 손님의 발길이 끊어지는 것을 기다리고 있었기나 한 것처럼, 모자 세 사람이 들어왔다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">형은 중학생 교복, 동생은 작년 형이 입고 있던 잠바를 헐렁하게 입고 있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">두 사람 다 몰라볼 정도로 성장해 있었는데, 그 아이들의 엄마는 색이 바랜 체크무늬 반코트 차림 그대로였다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;어서 오세요!&quot; 라고 웃는 얼굴로 맞이하는 여주인에게, 엄마는 조심조심 말한다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;저...... 우동...... 이인분인데도...... 괜찮겠죠?&quot; &quot;넷...... 어서 어서. 자 이쪽으로.&quot; 라며 2번 테이블로 안내하면서, 여주인은 거기 있던 &lt;예약석&gt;이란 팻말을 슬그머니 감추고 카운터를 향해서 소리친다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;우동 이인분!&quot; 그걸 받아, &quot;우동 이인분!&quot; 이라고 답한 주인은 둥근 우동 세 덩어리를 뜨거운 국물 속에 던져 넣었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">두 그릇의 우동을 함께 먹는 세 모자의 밝은 목소리가 들리고, 이야기도 활기가 있음이 느껴졌다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">카운터 안에서, 무심코 눈과 눈을 마주치며 미소짓는 여주인과, 예의 무뚝뚝 한 채로 응응, 하며 고개를 끄덕이는 주인이다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;형아야, 그리고 쥰아...... 오늘은 너희 둘에게 엄마가 고맙다고 인사하고 싶구나.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;......고맙다니요...... 무슨 말씀이세요?&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;실은, 돌아가신 아빠가 일으켰던 사고로, 여덟 명이나 되는 사람이 부상을 입었잖니.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">보험으로도 지불할 수 없었던 만큼을, 매월 5만엔씩 계속 지불하고 있었단다.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;음------ 알고 있어요.&quot; 라고 형이 대답한다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">여주인과 주인은 몸도 꼼짝 않고 가만히 듣고 있다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;지불은 내년 3월까지로 되어 있었지만, 실은 오늘 전부 지불을 끝낼 수 있었단다.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;넷! 정말이에요? 엄마!&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;그래, 정말이지.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">형아는 신문배달을 열심히 해주었고, 쥰이 장보기와 저녁 준비를 매일 해준 덕분에, 엄마는 안심하고 일할 수 있었던 거란다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그래서 정말 열심히 일을 해서 회사로부터 특별수당을 받았단다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그것으로 지불을 모두 끝마칠 수 있었던 거야.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;엄마! 형! 잘됐어요! 하지만, 앞으로도 저녁 식사준비는 내가 할 거예요.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;나도 신문배달, 계속할래요. 쥰아! 힘을 내자!&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;고맙다. 정말로 고마워.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">형이 눈을 반짝이며 말한다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;지금 비로소 얘긴데요, 쥰이하고 나, 엄마한테 숨기고 있는 것이 있어요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그것은요...... 11월 첫째 일요일, 학교에서 쥰이의 수업 참관을 하라고 편지가 왔었어요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 때, 쥰은 이미 선생님으로부터 편지를 받아놓고 있었지만요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">쥰이 쓴 작문이 북해도의 대표로 뽑혀, 전국 콩쿠르에 출품되게 되어서 수업 참관일에 이 작문을 쥰이 읽게 됐대요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">선생님이 주신 편지를 엄마에게 보여드리면...... 무리를 해서 회사를 쉬실 걸 알기 때문에 쥰이 그걸 감췄어요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그걸 쥰의 친구들한테 듣고...... 내가 참 관일에 갔었어요.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;그래...... 그랬었구나...... 그래서.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;선생님께서, 너는 장래 어떤 사람이 되고 싶은가, 라는 제목으로, 전원에게 작문을 쓰게 하셨는데, 쥰은 &lt;우동 한그릇&gt;이라는 제목으로 써서 냈대요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">지금부터 그 작문을 읽어드릴께요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&lt;우동 한그릇&gt;이라는 제목만 듣고, 북해정에서의 일이라는 걸 알았기 때문에......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">쥰 녀석 무슨 그런 부끄러운 얘기를 썼지! 하고 마음 속으로 생각했죠.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">작문은...... 아빠가 교통사고로 돌아가셔서 많은 빚을 남겼다는 것, 엄마가 아침 일찍부터 밤 늦게까지 일을 하고 계시다는 것, 내가 조간석간 신문을 배달하고 있다는 것 등...... 전부 씌어 있었어요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그리고서 12월 31일 밤 셋이서 먹을 한 그릇의 우동이 그렇게 맛있었다는 것......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">셋이서 다만 한 그릇밖에 시키지 않았는데도 우동집 아저씨와 아줌마는, 고맙습니다! 새해엔 복 많이 받으세요! 라고 큰 소리로 말해주신 일.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 목소리는...... 지지 말아라! 힘내! 살아갈 수 있어! 라고 말하는 것 같은 기분이 들었다고요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그래서 쥰은, 어른이 되면, 손님에게 힘내라! 행복해라! 라는 속마음을 감추고, 고맙습니다! 라고 말할 수 있는 일본 제 일의 우동집 주인이 되는 것이라고, 커다란 목소리로 읽었어요.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">카운터 안쪽에서, 귀를 기울이고 있을 주인과 여주인의 모습이 보이지 않는다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">카운터 깊숙이 웅크린 두 사람은, 한 장의 수건 끝을 서로 잡아당길 듯이 붙잡고, 참을 수 없이 흘러 나오는 눈물을 닦고 있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;작문 읽기를 끝마쳤을 때 선생님이, 쥰의 형이 어머니를 대신해서 와 주었으니까, 여기에서 인사를 해달라고 해서......&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;그래서 형아는 어떻게 했지?&quot; &quot;갑자기 요청 받았기 때문에, 처음에는 말이 안 나왔지만......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">여러분, 항상 쥰과 사이좋게 지내줘서 고맙습니다......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">동생은 매일 저녁식사 준비를 하고 있습니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그래서 클럽 활동 도중에 돌아가니까, 폐를 끼치고 있다고 생각합니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">방금 동생이 &lt;우동 한그릇&gt;이라고 읽기 시작했을 때...&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">나는 처음엔 부끄럽게 생각했습니다......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그러나, 가슴을 펴고 커다란 목소리로 읽고 있는 동생을 보고 있는 사이에, 한 그릇의 우동을 부끄럽다고 생각하는 그 마음이 더 부끄러운 것이라고 생각했습니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그때......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">한 그릇의 우동을 시켜주신 어머니의 용기를 잊어서는 안된다고 생각합니다......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">형제가 힘을 합쳐, 어머니를 보살펴 드리겠습니다......&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">앞으로도 쥰과 사이좋게 지내주세요 라고 말했어요.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">차분하게 서로 손을 잡기도 하고, 웃다가 넘어질 듯이 어깨를 두드리기도 하고, 작년까지와는 아주 달라진 즐거운 그믐날 밤의 광경이었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">우동을 다 먹고 300엔을 내며 &#39;잘 먹었습니다.&#39;라고 깊이깊이 머리를 숙이며 나가는 세 사람을, 주인과 여주인은 일년을 마무리하는 커다란 목소리로, &#39;고맙습니다! 새해엔 복 많이 받으세요!&#39;라며 전송했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">다시 일년이 지나 ------ 북해정에서는, 밤 9시가 지나서부터 &lt;예약석&gt;이란 팻말을 2번 테이블 위에 올려놓고 기다리고 기다렸지만, 그 세 모자는 나타나지 않았다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">다음 해에도, 또 다음 해에도, 2번 테이블을 비우고 기다렸지만, 세 사람은 끝내 나타나지 않았다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">북해정은 장사가 번창하여, 가게 내부수리를 하게 되자, 테이블이랑 의자도 새로이 바꾸었지만 그 2번 테이블만은 그대로 남겨두었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">새 테이블이 나란히 있는 가운데에서, 단 하나 낡은 테이블이 중앙에 놓여 있는 것이다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;어째서, 이것이 여기에?&quot; 하고 의아스러워하는 손님에게, 주인과 여주인은 &lt;우동 한그릇&gt;의 일을 이야기하고, 이 테이블을 보고서 자신들의 자극제로 하고 있다, 어느 날인가 그 세 사람의 손님이 와줄지도 모른다, 그 때 이 테이블로 맞이하고 싶다, 라고 설명하곤 했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 이야기는, &#39;행복의 테이블&#39;로써, 이 손님에게서 저 손님에게로 전해졌다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">일부러 멀리에서 찾아와 우동을 먹고 가는 여학생이 있는가 하면, 그 테이블이 비길 기다려 주문을 하는 젊은 커플도 있어 상당한 인기를 불러 일으켰다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그리고 나서 또, 수년의 세월이 흐른 어느 해 섣달 그믐의 일이다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">북해정에는, 같은 거리의 상점회 회원이며 가족처럼 사귀고 있는 이웃들이 각자의 가게를 닫고 모여들고 있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">북해정에서 섣달 그믐의 풍습인 해 넘기기 우동을 먹은 후, 제야의 종소리를 들으면서 동료들과 그 가족이 모여 가까운 신사에 그 해의 첫 참배를 가는 것이 5,6년 전부터의 관례가 되어 있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 날 밤도 9시 반이 지나 생선가게 부부가 생선회를 가득 담은 큰 접시를 양손에 들고 들어온 것이 신호라도 되는 것처럼, 평상시의 동료 30여명이 술이랑 안주를 손에 들고 차례차례 모여들어 가게 안의 분위기는 들떠있었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">2번 테이블의 유래를 그들도 알고 있다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">입으로 말은 안해도 아마, 금년에도 빈 채로 신년을 맞이할 것이라고 생각했지만 &#39;섣달 그믐날 10시 예약석&#39;은 비워 둔 채 비좁은 자리에 전원이 조금씩 몸을 좁혀 앉아 늦게 오는 동료를 맞이했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">우동을 먹는 사람, 술을 마시는 사람, 서로 가져온 요리에 손을 뻗치는 사람, 카운터 안에 들어가 돕고 있는 사람, 멋대로 냉장고를 열고 뭔가 꺼내고 있는 사람등등으로 떠들썩하다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">바겐세일 이야기, 해수욕장에서의 에피소드, 손자가 태어난 이야기 등, 번잡 함이 절정에 달한 10시 반이 지났을 때, 입구의 문이 드르륵 하고 열렸다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">몇사람인가의 시선이 입구로 향하며 동시에 그들은 이야기를 멈추었다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">오바를 손에 든 정장 슈트 차림의 두 사람의 청년이 들어왔다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">다시 얘기가 이 어지고 시끄러워졌다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">여주인이 죄송하다는 듯한 얼굴로 &quot;공교롭게 만원이어서&quot; 라며 거절하려고 했을 때 화복(일본 옷) 차림의 부인이 깊이 머리를 숙이며 들어 와서, 두 청년 사이에 섰다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">가게 안에 있는 모두가 침을 삼키며 귀를 기울인다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">화복을 입은 부인이 조용히 말했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;저...... 우동...... 3인분입니다만...... 괜찮겠죠?&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 말을 들은 여주인의 얼굴색이 변했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">십수 년의 세월을 순식간에 밀어 젖히고, 그 날의 젊은 엄마와 어린 두 아들의 모습이 눈앞의 세 사람과 겹쳐진다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">카운터 안에서 눈을 크게 뜨고 바라보고있는 주인과, 방금 들어온 세 사람을 번갈아 가리키면서, &quot;저...... 저...... 여보!&quot; 하고 당황해하고 있는 여주인에게 청년 중 하나가 말했다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">&quot;우리는, 14년전 섣달 그믐날 밤, 모자 셋이서 일인분의 우동을 주문했던 사람입니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 때의 한 그릇의 우동에 용기를 얻어 세 사람이 손을 맞잡고 열심히 살아갈 수가 있었습니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 후, 우리는 외가가 있는 시가현으로 이사했습니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">저는 금년, 의사 국가시험에 합격하여 교토의 대학병원에 소아과의 병아리 의사로 근무하고 있습니다만, 내년 4월부터 삿뽀로의 종합병원에서 근무하게 되었습니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그 병원에 인사도 하고 아버님 묘에도 들를겸 해서 왔습니다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">그리고 우동집 주인은 되지 않았습니다만 교토의 은행에 다니는 동생과 상의해서, 지금까지 인생 가운데에서 최고의 사치스러운 것을 계획했습니다...... 그것은, 섣달 그믐 날 어머님과 셋이서 삿뽀로의 북해정을 찾아와 3인분의 우동을 시키는 것이었습니다.&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">고개를 끄덕이면서 듣고 있던 여주인과 주인의 눈에서 왈칵 눈물이 넘쳐 흘렀다.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">입구에 가까운 테이블에 진을 치고 있던 야채가게 주인이, 우동을 입에 머금은 채 있다가 그대로 꿀꺽하고 삼키며 일어나, &quot;여봐요 여주인 아줌마! 뭐하고 있어요! 십년간 이 날을 위해 준비해 놓고 기다리고 기다린, 섣달 그믐날 10시 예약석이잖아요, 안내해요.&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">안내를!&quot; 야채가게 주인의 말에 번뜩 정신을 차린 여주인은, &quot;잘 오셨어요...&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">... 자 어서요...... 여보! 2번 테이블 우동 3인분!&quot; 무뚝뚝한 얼굴을 눈물로 적신 주인, &quot;네엣! 우동 3인분!&quot;&nbsp;</span><br />\r\n<span style=\"background-color:rgb(249, 250, 244); color:rgb(105, 105, 105); font-family:applesdgothicneo-regular,나눔고딕,nanumgothic,맑은고딕,malgun gothic,dotum,돋움,dotum,helvetica; font-size:15px\">예기치 않은 환성과 박수가 터지는 가게 밖에서는 조금 전까지 흩날리던 눈발도 그치고, 갓 내린 눈에 반사되어 창문의 빛에 비친 &lt;북해정&gt;이라고 쓰인 옥호막이 한 발 앞서 불어제치는 정월의 바람에 휘날리고 있었다</span></p>\r\n', '2017-08-30 12:06:33', '구리 료헤이', '2', '0', '1', '0', null, '1');
INSERT INTO `t_artical` VALUES ('60', '서시', '<p>죽는 날까지 하늘을 우러러</p>\r\n\r\n<p>한 점 부끄러움이 없기를</p>\r\n\r\n<p>잎새에 이는 바람에도</p>\r\n\r\n<p>나는 괴로워했다.</p>\r\n\r\n<p>별을 노래하는 마음으로</p>\r\n\r\n<p>모든 죽어가는 것을 사랑해야지.</p>\r\n\r\n<p>그리고 나한테 주어진 길을</p>\r\n\r\n<p>걸어가야겠다.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>오늘 밤에도 별이 바람에 스치운다.</p>\r\n', '2017-08-30 12:11:49', '윤동주', '1', '0', '1', '1', '20170830121149.jpg', '1');
INSERT INTO `t_artical` VALUES ('61', '청산별곡', '<p>살어리 살어리랏다 쳥산(靑山)애 살어리랏다</p>\r\n\r\n<p>멀위랑&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E38A.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />래랑 먹고 쳥산(靑山)애 살어리랏다</p>\r\n\r\n<p>얄리 얄리 얄랑셩 얄라리 얄라</p>\r\n\r\n<p>우러라 우러라 새여 자고 니러 우러라 새여</p>\r\n\r\n<p>널라와 시름 한 나도 자고 니러 우리노라</p>\r\n\r\n<p>얄리 얄리 얄라셩 얄라리 얄라</p>\r\n\r\n<p>가던 새 가던 새 본다 믈아래 가던 새 본다</p>\r\n\r\n<p>잉무든 장글란 가지고 믈아래 가던 새 본다</p>\r\n\r\n<p>얄리 얄리 얄라셩 얄라리 얄라</p>\r\n\r\n<p>이링공 뎌링공&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/F537.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />야 나즈란 디내와손뎌</p>\r\n\r\n<p>오리도 가리도 업슨 바므란&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/EAAB.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />&nbsp;엇디 호리라</p>\r\n\r\n<p>얄리 얄리 얄라셩 얄라리 얄라</p>\r\n\r\n<p>어듸라 더디던 돌코 누리라 마치던 돌코</p>\r\n\r\n<p>믜리도 괴리도 업시 마자셔 우니노라</p>\r\n\r\n<p>얄리 얄리 얄라셩 얄라리 얄라</p>\r\n\r\n<p>살어리 살어리랏다 바<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E46C.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />래 살어리랏다</p>\r\n\r\n<p><img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E283.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" /><img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E560.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />자기 구조개랑 먹고 바<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E46C.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />래 살어리랏다</p>\r\n\r\n<p>얄리 얄리 얄라셩 얄라리 얄라</p>\r\n\r\n<p>가다가 가다가 드로라 에졍지 가다가 드로라</p>\r\n\r\n<p>사<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E97D.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />미&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/F1EB.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />대에 올아셔&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/F550.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />금을 혀거를 드로라</p>\r\n\r\n<p>얄리 얄리 얄라셩 얄라리 얄라</p>\r\n\r\n<p>가다니&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E668.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />브론 도긔 설진 강수를 비조라</p>\r\n\r\n<p>조롱곳 누로기&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E57B.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />와 잡<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/E97D.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />와니 내 엇디&nbsp;<img src=\"http://t1.daumcdn.net/language/unicode/hanyang-pua/F537.png\" style=\"border:0px none; padding:0px 5px; position:relative; top:5px\" />리잇고</p>\r\n\r\n<p>얄리 얄리 얄라셩 얄라리 얄라</p>\r\n\r\n<div class=\"autosourcing\" style=\"margin: 0px; padding: 0px; position: fixed; top: -1000px;\"><br />\r\n<br />\r\n<span style=\"font-size:12px\">[Daum백과]&nbsp;<a href=\"http://100.daum.net/encyclopedia/view/14XXE0056472\" style=\"color: blue;\">청산별곡</a>&nbsp;&ndash;&nbsp;<a href=\"http://100.daum.net/book/624\" style=\"color: blue;\">한국민족문화대백과사전</a>, 한국학중앙연구원<br />\r\n본 콘텐츠의 저작권은 저자 또는 제공처에 있으며, 이를 무단으로 이용하는 경우 저작권법에 따라 법적 책임을 질 수 있습니다.</span></div>\r\n\r\n<p>&nbsp;</p>\r\n', '2017-08-30 12:13:39', '미상', '1', '0', '0', '0', null, '1');
INSERT INTO `t_artical` VALUES ('62', '손님에게 반한 카페 알바 이야기', '<p style=\"text-align:justify\"><span style=\"color:rgb(154, 84, 206); font-family:나눔고딕,nanumgothic; font-size:12pt\"><strong><em>첫번째 글</em></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">평소에도 생각하곤 했지만 당신은 잘생긴 편은 아니다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">디스하는 게 아니고,&nbsp;</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">신기해서 자꾸 말하게 된다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내가 줄곧 찬양하던 각종 연예인의 얼굴과는 너무도 동떨어진, 흔한 외모인 게.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내 글이 조금 고루하고 너저분할 수도 있지만, 잘생기지 않은 당신에 대해서 적어보려 한다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">유난히 덥던 이번 여름, 나는 카페 아르바이트를 시작했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">평소에 내가 싫어하던 브랜드의 카페인지라 알바가기가 많이 귀찮았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래도 또 구하기는 더 귀찮</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">아서 그냥 나가기로 마음먹었다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그날도 귀찮음을 무릅쓰고 출근해서 카운터에 앉아 웹툰을 보고 있었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 당신이 걸어들어왔다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&#39;내 또래로 보이는데 부지런하네-아르바이트만 아니었다면,&nbsp;</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">9시는 나에게 한밤중이었을 것이다-&#39;하는 생각에 흘깃흘깃 쳐다봤다</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">눌러&nbsp;쓴 베이지색 모자에 평범한 반팔, 평범한 반바지, 수더분한 외모.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 꽤 좋은 목소리로 &#39;안녕하세요&#39;하며 고개를 꾸벅.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-주문 도와드릴까요?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-네 아이스 카페모카요.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신은 얌전히 앉아 기다렸다 &nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">모자와 옷 색깔 때문에 커다란 리트리버 같았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나온 음료를 받아들고는 감사합니다,라고 중얼거리고 꾸벅하더니 총총 사라졌다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그&nbsp;날의 당신은 그냥 수많은, 평범한 손님 중 한 명이었다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그 다음 날도 당신은 비슷한 시간에 왔다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">문을 당기려고 힘을 줬다가 &#39;미시오&#39;를 봤는지,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">어정쩡하게 미는 바람에 종소리가 요란했다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">죄송합니ㄷ...&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신은 종소리에 놀라 기어들어가는 목소리로 사과했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&#39;소심하네&#39;라고 생각했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">소심한 건 딱 질색이다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신은 어제와 같은 음료를 시켜놓고 기다렸다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">동아리 같은 걸 하나보다,라고 스치듯 생각하고 커피를 만들었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나온 커피를 양손으로 받아든 당신은 나를 보고 어제처럼 꾸벅 인사하고 나갔다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">뭔가 내가 형식적으로 하는 인사가 민망하게 느껴질 정도로 사람&nbsp;냄새&nbsp;나는 인사였다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그 다음 날도 왔다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이번엔 문을 제대로 밀고 들어왔고,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">기분&nbsp;탓인지 모르겠지만 묘한 뿌듯함이 보여 속으로 웃었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">잘생기진 않았지만, 표정이 풍부하다고 생각했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그 전의 날들과 같은 음료를 주문했고, 같은 자리에서 얌전히 기다리다가 같은 방향으로 사라졌다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">학교나 집이 그곳인가 했는데, 몇 시간 뒤 우연히 당신이 아침에 사라진 방향과 반대 방향으로 걸어가는 것을 봤다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내일은 어디로 가나 봐야겠다고 혼자 다짐했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 그런 날 놀리듯이 당신은 며칠 보이지 않았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">인정하기 싫은데 꽤 궁금했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">손님도 몇 없었고, 뭔가 당신에 대해 추리하는 게 좀 재밌을 성 싶었는데.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">일주일 쯤 지났을까 당신이 들어왔다</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">머리를 했는지 하늘색 모자 사이로 삐친 머리카락들이 구불거렸다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">못내 반가웠지만,&nbsp;</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">알지도 못하는 사람을 반가워한다는 사실을 인정하기 싫어서 &#39;이 반가움은 추리를 계속 할 수 있다는 반가움이야!&#39;라고 생각했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">음료를 기다리며 당신은 전화를 했다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-응, 예 그렇습니다, 며칠 뒤에 내려감다, 아 잘 챙겨먹고 있져, 아이고 걱정마시져, 응 영화보려고 나왔어, 아니 있어 걱정마, 어제? 어제 조금만 마셨어 진짜로, 아이 믿으시죠, 응, 응 이따 또 하겠슴다 예 쉬십셔</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">아마 부모님 같았은데, 말투가 친근해서 귀엽다는 생각을 했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 말 사이사이 낮게 웃을 때의 웃음소리가,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">어... 꽤, 매력있었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 역시 잘생긴 편은 아니지만, 너스레를 떨 때 나오는 표정은 묘하게 귀여웠다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나도 모르게 자꾸 찾게 되는 당신의 매력을 인정하는 게 민망해서 &#39;영화를 보는 거구나, 커피는 영화보면서 마시나보네&#39;&nbsp;하는 생각들을, 그리고 당신의 웃음소리를 컵 속에 넣어버렸다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">평소처럼 음료를 받은 당신은 인사를 하다말고 잠시 머뭇거리더니 한 잔을 더 주문했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">캐리어가 필요없다고 말한 당신은 카페 밖에 나가 방금 받은 커피를 목에 털어넣고 돌아왔다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">되게 멋쩍어하면서 다음 커피를 기다리는 모습이 좀 웃겼다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">혹시 술 때문인가 라는 생각이 들어서 초코를 조금 더 넣었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이&nbsp;정도 호의는 누구나 베푸는 거지!라고 생각하며.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 조금 더 친절하게 인사했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">형식적으로 들리지 않게, 톤도 부드럽게, 멘트도 다르게.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-감사합니다 또 오세요</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-어, 아, 네, 감사합니다 꾸벅꾸벅</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">받은 인사가 황송하다는 듯이 연신 고개를 숙이며 나가는 당신의 모습을 빤히 바라봤다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그냥 봤다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">묘하게 영화관 쪽으로 사라지는 뒷모습이 아쉬웠다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이런 내 마음을 아는 건지, 두세시간 후 당신은 돌아왔다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">책을 들고.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">같은 커피를 시켜, 조금 안쪽 자리에서 몇 시간 쯤 책을 읽었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신이 책을 읽는 그 몇 시간 동안, 난 점점 많은 게 궁금해졌다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">무얼 더 주문해 먹지도 않는데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">뭘 먹고 들어온 게 아니면 커피 세 잔이 먹은 것의 전부일텐데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">배는 안고픈가 궁금했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">무슨 책을 읽는지 궁금했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">오늘은 무슨 영화를 봤는지 묻고 싶었고, 영화에 대해 이야기하고 싶었다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">정말 웃기게도 당신은 내 스타일이 아닌데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내가 좋아할 만큼 잘생기지 않았는데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">난 당신을 고작 아르바이트생으로서 본 게 전부인데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">몽글몽글한 기분이 들었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">스스로 어이가 없었고,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">쓸데없이 등판한 개똥같은 자존심 때문에&nbsp;</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그&nbsp;</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">뒤로도 꽤 많은 시간을 당신에 대해 궁금해하기만 하며 보냈다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">무더운 여름의 끝자락에서 나는 &#39;내가 말건다고? 내가 왜? 아니 솔직히 나 정도면 말 걸고 싶은 여자 아닌가?&#39;라는 개똥같은 생각과 자존심을 버리기로 했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">진짜 개똥에 대기도 미안한 바보같은 생각이었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">대신 궁금증을 핑계삼았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이것저것 궁금해서 묻지 않고는 못배기겠노라고, 그렇게 여기기로 했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 얄궂게도 당신은 내가 행동하기로 마음 먹은 순간부터 나타나지 않았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">정말 내가 환상을 봤다는 듯이 당신은 발길을 뚝 끊었다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">개강이 다가올 즈음, 나는 알바를 그만두었고 대신 그 주변을 맴돌았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그 카페에 죽치기도 하고, 그 근처 영화관에서 영화를 보고, 나오는 사람의 얼굴을 일일이 훔쳐보기도 했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">집이 근처일 것 같아 노래를 들으며 거리를 몇 시간씩 걷기도 했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그런 일련의 과정 속에서 수없이 짜증이 났고&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 &#39;아 더럽게 비싼 인연인가 보네&#39;라고 털어내려고도 해보았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">근데 생각보다 쉽지 않았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">연애를 한 것도 아닌데 서글펐고 화가 났다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">한 번 만나보기라도 했으면 그때 느낀 단점을 계속 상기하면서 밀어낼텐데&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그것도 안되는 상황이고, &#39;당신은 신포도야&#39;라고 다독이기엔 너무 많은 게 궁금했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">미친..&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">만나본 것도 아닌데 자꾸 궁금증을 빙자한 내 마음만 부풀었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">잘생기지 않은 그 얼굴을 계속 까내리려고 해도, 어느 샌가 그 얼굴은 웃고 있었다 매력있게.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">하, 당신이 다시 태연하게 내 앞에 나타났으면 좋겠다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이젠 인정하기 싫다는 말은 하지 않을테니까.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">인정한다 당신이 보고싶다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">매력있다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내 기억 속에서 조금씩 각색까지 이루어져서,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">지금 나에게 당신은 더럽게 매력터진다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">지금 오면 내가 마음 죄다 꺼내줄 것 같다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">태연하게 와서 카페모카 달라고 해라</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">백 잔, 천 잔도 사줄테니 같이 마시자</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">난 요즘도 그 카페에서</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내 입에도 안맞는 카페모카를 마신다</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">언제까지 이럴지는 모르겠지만</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내가 잠겨죽기 전에</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나에게 밀려왔으면 좋겠다</span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(154, 84, 206); font-family:나눔고딕,nanumgothic; font-size:12pt\"><em><strong>그리고 그에 대한 답장!&nbsp;</strong></em></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">저도 길게 적고 싶은데, 글재주가 없어 그러지 못해서 미안해요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">한동안 SNS를 끊고 살았었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">근데 며칠 전에 동생이 이거 너 아니냐,라고 웃으면서 뭘 보여주더라구요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">긴 글이었고, 재밌는 글이라고 생각했고, 뭔가 익숙했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">잘생기진 않은 외모, 하늘색 모자, 꾸벅, 카페모카.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">어쩌면 김칫국일지도 몰라요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">옆에서 호들갑 떠니까 저도 모르게 이것저것 썰어넣고 보글보글 끓이는.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 사실 엄청 망설였어요. 민망한 일이 생길까봐.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">근데 제가 자주 다니던, 기억하는 그 카페에 계셨던 분이 적은 글일까봐, 민망함을 무릅쓰고 제보 보내요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">제보가 처음이라 약관...?같이 긴 부분들을 읽어봤는데, 여러가지 면에서 사람을 찾는 듯한 뉘앙스는 안되는 것 같더라구요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그렇다고 실명을 밝히기엔&nbsp;(제가 생각하는 분과 맞는 분이든 아니든)&nbsp;제가 너무 민망할 것 같구요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 익명으로나마 짧은 답장을 보내고 싶어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">익숙지 않아서 규칙에 위배되는 부분이 있다면 올라가지 않겠지만, 일단 적어 보내봐요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">저는 그 브랜드의 카페를 좋아해요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">친숙한 느낌이거든요!&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">커피를 잘 몰라서 달달한 것만 골라서 마셔요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그러다가 정착한 게 카페모카예요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">초코 많이 넣어주신 거 정말 감사했습니다.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">실제로 속이 너무 쓰려서 두 잔 마신 거였어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">제가 거의 첫손님으로 찾아가는 느낌이라, 그냥 혹시나 제가 귀찮게 하는 부분이 있을까봐 조심스러웠어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">실제로 조금 피곤해 보이시기도 했구요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">영화는 그냥 아무거나 전부 봤어요. 가리지 않고 다 좋아하거든요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">읽던 책은 &#39;13계단&#39;이라는 다카노 가즈아키의 소설이에요. 근처 중고매장에서 샀어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 걱정...?해주신 대로 밥을 잘 안먹었어요 그때는.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">귀찮아서 그냥 아침에 커피 한 잔, 점심에 커피 두 잔.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">보신 대로 잘생기진 않았고, 약간 소심한 것 같네요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 친절한 인사에 제대로 대꾸하지 못했고, 조금 큰 종소리에도 죄송하다고 했어요. &nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 오늘 밤 비행기를 타요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">유럽으로 한 달이 조금 안되게 여행을 가거든요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">고등학교부터 대학교, 군대까지 너무 쉬지 않고 달린 것 같아서 여유를 가져보고 싶었어요. &nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">하필이면 이런 타이밍에 가네요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">될 사람은 된다더니, 전 안 될 사람인가 싶기도 하고.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">사실 서글서글한 눈웃음이, 커피를 건낼 때 조금씩 스치던 손이, 씩씩하면서 상냥한 목소리가 너무 좋았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">근데 보시다시피 제가 적극적이지 못한 탓에..&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 조금 더 사실은 &#39;어떻게 나 같은 애가 저런 분을&#39;이라는 생각까지 했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">더 자주 갈 걸..&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이 답장을 쓰는 데도 입술 여러 번 깨물었어요. 민망하고 쑥쓰럽고 미안해서.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 고마워요. 좋게 봐주셔서.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이 다음에 적을 말은 지울까 말까를 정말 한참을 고민했는데, 이왕 쓴 거 그냥 적어서 보낼게요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">10월 마지막 주에, 저는 내내 그 카페에서 있을 생각이에요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">운이 좋다면 이 글을 보실테고, 조금 더 운이 따라준다면 아직 제가 밀려갈 수 있겠죠.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">카페모카 말고, 좋아하시는 커피 사드리고 싶어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">여름의 끝자락에서 절 기다리셨듯이,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이번에는 제가 기다릴게요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">겨울의 초입새에서.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(154, 84, 206); font-family:나눔고딕,nanumgothic; font-size:12pt\"><strong><em>그리고 마지막 완결편 ㅋ</em></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">소설을 읽다보면, 이게 도대체 어떻게 끝나려나 싶은 소설들이 있다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">마지막 책장을 넘길 즈음에는 아쉬움이 남는 소설이 있고, 황당함이나 불쾌함이 짙게 깔리는 소설도 있다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">깊은 여운이 어깨를 쓸어내리는 바람에 한동안 멍하니 허공을 바라보게 하는 소설도 있을 것이고,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">파문 하나 없이 고요한 느낌을 주는 소설도 있을 것이다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">개인적으로는 차마 책장을 넘기지 못하겠는, 그런 아쉬움이나 슬픔이 남는 결말을 좋아한다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">모든 게 생각대로 완벽하게 흐르고 진행되면, 어쩐지 그 행복이 쉽게 날아가 버리는 느낌이 들어서.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">고생 끝에 낙이 온다는데, 고생할수록 그 낙은 좀 더 달지 않겠나.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">뭐, 그렇게 생각했다 지금까지는.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">좁은 비행기 좌석 정면에 &#39;00:20&lsquo;이라는 글자가 깜빡거려요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">20분이면 한국에 도착한다고 말해주는 거겠죠.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">시간이 빨라요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">덮어놓고 저지른 일을 받아들이기엔 아직 마음의 준비가 덜 된 것 같아서.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">사실 많이 떨려요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">기분 좋은 떨림이 아니라, 내가 당신이 생각하고 바라는 그런 사람이 아니어서 내쳐질까 봐요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">돌아오는 비행기에서 내내 손톱을 뜯고 다리를 떨었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">고쳤다고 생각했던 버릇이 툭툭 쏟아지는 것을 보니 지금 많이 조급하긴 한가 봐요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">뜯은 손톱이 흉해서 당신을 만날 때는 손을 아래로 내리고 있어야겠다는 생각을 할 즈음,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">저는 한국에 도착했어요.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">눈앞의 소설이 어떤 감정을 남길지 또는 그 감정이 쉬이 날아갈 것인지 아닌지 알고 싶다면&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-&nbsp;작가가 아닌 이상에야&nbsp;-,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">일단 끝까지 읽어보는 수밖에 없다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">지루하거나(이놈의 클리셰),&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">가독성이 떨어지거나(왜 이리 들쭉날쭉이야),&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">주인공이 답답해도(어휴 엄청 물러 터졌네),&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">일단은 참아야한다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이렇게 적어놓고 보니 독자는 일면 을의 입장인 것 같기도 하다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">간만에 느껴보는 편안함이었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">주변에서 소매치기니 한국인 차별이니 조심하라는 당부의 말을 많이 해주셔서&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">가뜩이나 소심한 제가 몸을 잔뜩 웅크리고 다녔거든요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">비행기에서 잠을 설친 탓에 집으로 가는 버스에서 정신없이 잤어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 여독인지, 된통 몸살이 들었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">마디마디가 너무 아려서 못 참겠다는 생각이 들 정도로 아팠어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 그날은 그냥 도착하자마자 짐도 못 풀고 누워버렸어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">정말 한국에 온 건지, 누워있는 침대가 호스텔의 침대는 아닌지 확인할 겨를도 없이&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">꿈결 따라 정신없이 걸었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">서울에서 당신을 기다려야하는데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이번엔 내가 기다리기로 했는데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">하늘이 쌀쌀한 걸 보니 지금이 내가 말한 그 겨울의 초입새가 맞는데.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">서럽게도 몸이 따라주질 않았어요.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나의 경우에는 &lt;해리포터&gt; 시리즈를 하나의 예로 들 수 있다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">어려서부터 이 시리즈를 굉장히 좋아했지만 안타깝게도 영문판을 척척 읽어낼 능력은 없었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">(물론 지금도 없다 주륵)&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 매번 영문판이 발간되더라도 번역판이 나올 때까지 기다려야 했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">특히나 기억에 남는 기다림은 5권 ; 불사조 기사단인데, 혹시나 스포일러일까 봐 조심스럽지만,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">등장인물 한 명이 베일 속으로 사라지는 장면이 있다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">책이 한 권씩 번역되어 들어온 터라 결정적인 장면에서 한껏 감정이입해서 울다가도 책장을 다 넘기면 무작정 몇 주, 몇 달을 기다려야하는 차디찬 현실로 내동댕이쳐지곤 했던 것이다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">얼마나 애가 탔는지는 글로 설명해도 한참 모자랄 것이다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">부모님이 아무리 &lsquo;어쩔 수 없으니까 잊고 지내다가 나오면 재밌게 읽자&rsquo;라고 다독여도,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 나조차도 그 말이 지극히 옳다고 생각하면서도,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">서툰 검색으로 하루에도 열&nbsp;두 번씩 인터넷 서점 페이지를 들락날락 거리는 일은 멈출 수가 없었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그만큼 기다림은 힘들고, 진한 것이었다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">꼬박 하루를 자고, 거진 이틀을 앓은 후에야 조금 나아졌어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">시차적응에 아직도 뎅-뎅 거리며 울리는 머리를 잡고, 서울에 가야겠다는 생각을 했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">가족들이 말렸어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">무슨 약속인지는 모르겠지만, 너무 아파보이니까 하루만 더 있다 가라고.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">저도 모르게 고개를 끄덕이고 자리에 다시 앉았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">하루 더 기다리게 한다는 생각에 미안했지만,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">거울을 보니 이 꼴로 마주하는 것도 미안할 짓이라는 생각이 들었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 문득 당신이 나오지 않을 수도 있겠다는 생각도 했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">런 생각이 든 이유는 모르겠지만, 그냥 꿈처럼 그렇게 지나가셨을 수도 있겠다고.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신 같은 분이 나를 기다릴 리가 없다고.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">다시 자려고 누웠다가, 문득 당신의 글을 읽고 싶어졌어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이런 말 유치하게 들릴 수도 있지만, 그냥, 읽으면 실감이 날 것 같았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">자꾸 소심하게 굴려는 내가 좀 힘을 내지 않을까.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 거기서 내 답장에 대한 당신의 다른 답장을 발견했어요.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&lt;해리포터&gt; 기다리던 그때의 꼬맹이보다 지금의 나는 키도 인내심도 훨씬 자랐을진데&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">기다림에 몸부림치는 것은 여전하다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">아니 어쩌면 더하다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">카페에 구비되어있던 냅킨을 잘게 찢고-아 물론 잔해는 내가 치웠다&nbsp;</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나도 한때 이곳에서 일했었으니까-읽지도 않을 책을 들추며 출입문을 흘깃거렸다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&lsquo;괜히 챙겨왔나&rsquo;라는 생각을 하며 멍하니 책을 바라보다 보니,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">문득 내 상황이 옛날과 비슷한 것 같다는 생각이 들었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">다음 내용이 궁금해서 미치겠어도 다음 권이 발간되지 않으면 이미 외울 정도인 전의 내용들을 곱씹으며 기다려야 하는 것이고, 상대방이 감감무소식이면 마찬가지로 홀로 애태우며 그가 남긴 말들을 찢은 냅킨조각마냥 주워섬기며 기다리는 수밖에 없다는 것이.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">잘 때가 아니라는 것을 그제야 깨달았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">다행히도 아직 늦은 시각이 아니었고, 도착하면 당신을 만날 수 있을 것 같았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신에게 가는 내내 나에게 온 그 두 번째 답장을 읽고 또 읽었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">읽으면 읽을수록 자려고 했던, 그리고 잠시나마 당신이 나오지 않을 거라는 생각을 했던 제 자신이 너무 창피해서 몸 둘 곳을 정하지 못하고 허둥거렸어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">잇새로 자꾸 중얼거렸어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">이불 찬다고들 하잖아요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">창피한 기억, &lsquo;왜 그랬지&rsquo;하는 기억을 떠올리면 반사적으로 말을 내뱉거나 행동하는.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그 카페로 가는 모든 걸음마다 후회하고 미안해하면서 자책했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&lsquo;내가 이렇지. 오자마자 확인했어야 하는데. 괜히 애매하게 말했나 봐. 날짜를 정했으면 안기다리셨을 텐데. 날도 추운데 괜히 여러 번 왔다 갔다 하셨겠네. 뭐라고 하지. 잤다고 해야 하나. 아팠다고 해야 하는 건가. 기회를 받으면 뭐 해. 일을 그르치려니까 이렇게도 되는구나. 바보처럼. 만약에 안 나오셨으면 어쩌지. 아니 사실 그래도 할 말 없지. 하 뭐가 이렇지. 서럽다. 미안해서 미치겠다. 나오셨으면 뭐라고 해야 하지. 인사를 해야 하나. 사과가 먼저겠지. 사과를 한다면.......&rsquo;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">후회와 미안함 따위를 커다랗게 굴리며 걸으니, 어느&nbsp;새 카페 앞이었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">미시오인지 당기시오인지는 확인할 겨를도 없었고, 그건 저에게 더 이상 중요한 게 아니었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">몇 달 전에는 당신이 서있던 자리에 낯선 사람이 서있었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">자꾸 떨리는 손을 맞잡다가, 보기 흉한 손톱이 보여 얼른 아래로 그러쥐었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">낯선 사람의 인사를 받는 둥 마는 둥 눈으로는 열심히 당신을 찾았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그&nbsp;때와 다를 것 없는 구조에, 사람도 얼마 없었는데도 자꾸 시선은 방황했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">얼레벌레 둘러보고, 다시 둘러보고 나서야 당신이 없다고 생각했고, 조금씩 진정할 수 있었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">지나치게 뛰는 가슴을 진정시키면서 문득, &lsquo;덥다&rsquo;라고 느꼈고 시원한 커피 한 잔을 마셔야겠다고 생각했어요.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">냅킨 찢기도, 책 들춰보는 것도, 사람 구경도&nbsp;-&nbsp;애초에 사람도 많지 않았다&nbsp;-&nbsp;질릴 즈음,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">딱 한 시간만 더 있다 가기로 마음먹었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">사실 이런 생각을 이른 오후부터 하긴 했지만 왠지 내가 나가면 서로 엇갈릴 것 같은 기분에 꾸물거렸었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">며칠 째인지는 사실 중요하지 않다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">언젠가는 오겠지 최소한 11월 전에는.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래도 &lsquo;고생 끝에 낙은 개뿔, 그냥 소설이든 뭐든 기다림, 헤어짐 같은 거 없이 해피엔딩이 최고야&rsquo;&nbsp;라고 생각하며 입을 비쭉거렸다.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">거의 다 마신 카페모카 컵을 치우기 위해 일어섰다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">냅킨 조각도 그러모았다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">한 손에는 카페모카 잔을, 다른 한 손에는 냅킨 조각을 들고 쓰레기통을 향해 걸었다.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">한 잔만 더 마실까?</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&lsquo;차라리 잘 된 거지. 내가 기다리는 편이 나아.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 그게 맞고. 그래, 이왕 왔으니까 커피 한 잔 마시면서 기다리다가 늦게 영화나 한 편 보고 들어가자.&rsquo;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">따위의 생각을 하며 지갑을 꺼내고 있었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 한 손에는 컵을, 다른 한 손에는 휴지조각을 들고 있는 당신과 눈이 마주쳤어요.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&lsquo;드디어 왔구나&rsquo;&nbsp;라고 생각했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그</span><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">리고 당신은 그대로였다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">추운 날씨에 약간 두꺼운 옷을 입어서인지 커다란 강아지 같다는 인상이 더욱 짙어졌다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&lsquo;안녕 리트리버&rsquo;라고 부르며 놀려주고 싶었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">머리가 많이 자랐다 구불구불.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나는 분명히 내 얼굴에게 &lsquo;침착함&rsquo;을 주문했는데,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">나도 모르는 사이에 내 얼굴 근육은 멋대로 당신을 반기기 시작했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">많이 기다린 걸 알아달라고 시위하듯이 입꼬리, 콧등, 광대, 눈꼬리, 보조개 어디하나 태만한 곳 없이 아주 열렬히, 당신을 반기고 있었다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">무슨 말을 해야 하지, 따위의 생각조차도 못했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">크게 한 대 맞은 것처럼 눈만 크게 떴어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신은 태연해 보였어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">묘하게 빙글빙글 웃는 것 같기도 했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">몇 달 만에 보는 건데, 당신은 여전히 매력적이었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">묶은 모습만 봤던 머리가 풀려있었어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">보조개가 있는지는 몰랐어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">생각보다 키가 작았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 감히 이런 말을 써도 될지는 모르겠지만, 사랑스러웠어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">차마 제가 말을 걸기가 미안할 정도로.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">무슨 말이라도 해보려고 계속 입을 열었는데, 결국 말은 못 꺼내고 속절없이 닫았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">마주하면 처음 꺼낼 말들, 혼자 연습 되게 많이 했는데.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">연습한 말들이 아니라고 해도, 할 말이 많았는데.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">기다리게 해서 미안하다고, 내 잘못이라고, 많이 기다렸느냐고,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">많이 보고 싶었다고.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-저기</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-네? 네?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-커피 마실래요?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-아...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">-카페모카 맛있는데.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내 말을 듣고, 그제야 당신은 웃기 시작했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">본 적 없는 환한 웃음.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 나도 그냥 웃었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">침착함 따위는 버린 지 오래였다</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">당신의 말을 듣고, 웃음을 보고 깨달았어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">제가 얼마나 바보 같은 걱정을 하고 있었는지를.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">자꾸 비집고 나오는 웃음을 참아가며 계산대에 섰어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">주문하시겠어요? 라는 물음에 대답하려고 입을 열었다가 당신을 쳐다봤어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그러자 당신이 웃음을 잔뜩 머금고 대답했어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&ldquo;아이스 카페모카 주세요, 두 잔이요!&rdquo; 눈으로 웃으며,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">&#39;두 잔&#39;을 강조하기 위해 손가락 두 개를 활짝 펴고 주문하는 당신을 보니까 너무 많은 것이 묻고 싶어졌어요.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그리고 당신의 눈을 보니, 저 역시 대답해야할 것이 한 두 개가 아니라는 게 느껴졌어요.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그렇게 커피를 주문하고, 우리는 자리에 앉아 꽤 긴 시간을 이야기했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">무슨 얘기를 했는지 물으신다면, 대답해드리는 것이 인지상정이겠다 하지만, 보면 바로 아시겠지만,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">너무 시시콜콜한 이야기들이라서&nbsp;(유럽엔 카페모카가 없었어요, 오, 그럼 뭐마셨는데요? 카푸치노요. 헐, 변심한 거? 무, 무슨 소리예요. 큭큭큭, 근데 우리 말 언제 놔요? 편하실 때 놓으셔요. 모카야! 헐! 큭큭큭)&nbsp;적는 것이 너무 민망하다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">물론 이렇게 후기를 적는 것조차도 민망하다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그래서 정말 간략하게 &lsquo;저희 만났습니다!&rsquo;&nbsp;라는 식의 내용만을 전달하려고 했었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그런데 그건 기다리신 분들에 대한 예의가 아닌 것 같았다.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">또 생각해보면 사실 우리가 만날 수 있었던 이유는 대나무숲과 구독자 여러분들 덕분이니,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">성실하게 작성하고 감사인사까지 전하는 게 맞는 것 같아서 각자의 시선에서 작성하고, 함께 다듬었다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">원래는 각자 제보를 보내려고 했으나, 굳이 두 번이나 대나무숲 지기들을 귀찮게 하는 일을 하는 것 같아서 글을 합쳤다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">합치는 과정에서 많은 분들이 &lsquo;소설 같다&rsquo;라고 하신 게 생각나서 소설처럼 구성해보려고 노력했다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">적어놓고 보니 괜히 했나 싶다 들쭉날쭉.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">구성은 이렇지만, 각색된 내용은 한 군데도 없다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">정말 나는 기다린 지 3일 째였고, 그는 정말 많이 아팠다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">내가 기다리면서 읽으려고 들고 갔던 책은 &lt;13계단&gt;이었고,&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothic; font-size:12pt\">그는 책을 보고 반색하며 집어 들다가 나에게 손톱을 들켰다&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:나눔고딕,nanumgothi', '2017-08-30 12:18:01', '미상', '5', '2', '1', '0', null, '1');
INSERT INTO `t_artical` VALUES ('63', '龍井이 그립다', '<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">꿈나라처럼</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">멀게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">아득하게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">생각하든</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;北間島</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">龍</span><span style=\"color:rgb(85, 85, 85)\">井</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">그러나</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">지금의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">내게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">있어서는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잊을내야잊을수없는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">가장</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">정다운</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">곧이되여있다</span><span style=\"color:rgb(85, 85, 85)\">.</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">내게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">없서서는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">아니될</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">사람이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그곧에잇다고해서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">또</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">마음이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">서로통하는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">동무가</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잇다고해서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그다지</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">못</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잊어하는것은</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">아니다</span><span style=\"color:rgb(85, 85, 85)\">.</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">勿論&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">내가</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">龍</span><span style=\"color:rgb(85, 85, 85)\">井</span><span style=\"color:rgb(85, 85, 85)\">을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">보고오기</span><span style=\"color:rgb(85, 85, 85)\">前</span><span style=\"color:rgb(85, 85, 85)\">에는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그들이있슴으로해서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">龍</span><span style=\"color:rgb(85, 85, 85)\">井</span><span style=\"color:rgb(85, 85, 85)\">이란</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">두</span><span style=\"color:rgb(85, 85, 85)\">字</span><span style=\"color:rgb(85, 85, 85)\">를</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">머릿속에삭여두엇거니와</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">정작</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">지난</span><span style=\"color:rgb(85, 85, 85)\">六</span><span style=\"color:rgb(85, 85, 85)\">월에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">내발로</span><span style=\"color:rgb(85, 85, 85)\">幾千里北國</span><span style=\"color:rgb(85, 85, 85)\">의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그곧을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">찾어갓다</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">온</span><span style=\"color:rgb(85, 85, 85)\">後</span><span style=\"color:rgb(85, 85, 85)\">에는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;一百八十餘日</span><span style=\"color:rgb(85, 85, 85)\">이란</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">긴날동안에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">하로도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">龍</span><span style=\"color:rgb(85, 85, 85)\">井</span><span style=\"color:rgb(85, 85, 85)\">을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잊어본날이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">없섯다</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">그</span><span style=\"color:rgb(85, 85, 85)\">異國情緖</span><span style=\"color:rgb(85, 85, 85)\">를</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">가득이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">담고잇는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">龍</span><span style=\"color:rgb(85, 85, 85)\">井市</span><span style=\"color:rgb(85, 85, 85)\">의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">거리거리</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">同胞愛</span><span style=\"color:rgb(85, 85, 85)\">를</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">이몸에넘치도록</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">부어주시든</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">여러분</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">한신들</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그</span><span style=\"color:rgb(85, 85, 85)\">風景&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그사랑을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잊을수가</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">있을까</span><span style=\"color:rgb(85, 85, 85)\">?</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그러나</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그중에서도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">못견듸게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그들을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;生覺</span><span style=\"color:rgb(85, 85, 85)\">하든때는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;伐草</span><span style=\"color:rgb(85, 85, 85)\">하러</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">갓다오든때이엇다</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">伐草</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">一年</span><span style=\"color:rgb(85, 85, 85)\">에한번식</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;先親</span><span style=\"color:rgb(85, 85, 85)\">이나</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;宗家</span><span style=\"color:rgb(85, 85, 85)\">의붉집웅에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">염치없이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">우북하게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">자라잇는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;雜草</span><span style=\"color:rgb(85, 85, 85)\">를</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">날이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">시퍼런</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">낫으로다가</span><span style=\"color:rgb(85, 85, 85)\">삭싹비여버리고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">새파란</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잔디지붕을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">바라보는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">떄의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">맑고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">개인</span><span style=\"color:rgb(85, 85, 85)\">-</span><span style=\"color:rgb(85, 85, 85)\">한</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">마음이란</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;異國</span><span style=\"color:rgb(85, 85, 85)\">에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">게시는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그들에게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그야말노</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">꿈에나</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">가저볼수잇는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">보배이기</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">때문에</span><span style=\"color:rgb(85, 85, 85)\">......</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">秋夕</span><span style=\"color:rgb(85, 85, 85)\">도지난</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">어떤날</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">병으로</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">수척하여진이몸을끄을고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;墓地</span><span style=\"color:rgb(85, 85, 85)\">에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">올나가서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;伐草</span><span style=\"color:rgb(85, 85, 85)\">를하고나니</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">느진가을바람이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">이마에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">송이저맷친</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">구슬땀을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">싯처주면서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">내품에다가</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">서</span><span style=\"color:rgb(85, 85, 85)\">-</span><span style=\"color:rgb(85, 85, 85)\">늘한</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;思友情</span><span style=\"color:rgb(85, 85, 85)\">를</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">안겨주고지내갓다</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">그때</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">나는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">비탈길을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">걸어나려오면서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;「</span><span style=\"color:rgb(85, 85, 85)\">지금의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">이마음을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">고히고히차서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그들에게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">선물로주고십구나</span><span style=\"color:rgb(85, 85, 85)\">....</span><span style=\"color:rgb(85, 85, 85)\">」</span></div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">하고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">중얼그려보았거든</span><span style=\"color:rgb(85, 85, 85)\">.</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그러드니만</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">요새는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그때보다도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">좀더</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;北國</span><span style=\"color:rgb(85, 85, 85)\">으로서의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">龍</span><span style=\"color:rgb(85, 85, 85)\">井</span><span style=\"color:rgb(85, 85, 85)\">이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그러워진다</span><span style=\"color:rgb(85, 85, 85)\">.</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">내가</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">갓슬때는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">여름도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">한창이엇건만</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">하</span><span style=\"color:rgb(85, 85, 85)\">-</span><span style=\"color:rgb(85, 85, 85)\">얀버들꽃이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">눈처럼</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">날넛다</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">商埠局公園</span><span style=\"color:rgb(85, 85, 85)\">안에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잇는어느</span><span style=\"color:rgb(85, 85, 85)\">料理店</span><span style=\"color:rgb(85, 85, 85)\">의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;別室</span><span style=\"color:rgb(85, 85, 85)\">에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">앉어서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">아이스크림을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">두그릇식이나</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">퍼넣고잇는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">우리의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">유리창밖에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">나붓나붓</span><span style=\"color:rgb(85, 85, 85)\">휘날니든</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그눈꼿</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">綠陰</span><span style=\"color:rgb(85, 85, 85)\">이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">덮힌</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;公園&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">길바닥과</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">나무나무아레</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">쌓여잇는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그</span><span style=\"color:rgb(85, 85, 85)\">積雪</span><span style=\"color:rgb(85, 85, 85)\">!</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">새</span><span style=\"color:rgb(85, 85, 85)\">-</span><span style=\"color:rgb(85, 85, 85)\">하야케</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">물이끼모양으로</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">덥혀잇든</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">녹지않는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">눈</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">날새는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">찌는듯</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">더우면서도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">정서는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">백설의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">경을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">나타내는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그미묘한</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">정경을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">龍</span><span style=\"color:rgb(85, 85, 85)\">井</span><span style=\"color:rgb(85, 85, 85)\">이아니니면다른곧에서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;果然&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">찾어볼수</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잇을것인가</span><span style=\"color:rgb(85, 85, 85)\">?</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">남국인</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">이</span><span style=\"color:rgb(85, 85, 85)\">木浦</span><span style=\"color:rgb(85, 85, 85)\">에도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">요새</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">몇일만은</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">흰눈을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">함박으로</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">퍼붓고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잇다</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">그러나</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">지붕과</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">장독</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그릇우에만</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">두껍게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">쌓일뿐</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;枯木</span><span style=\"color:rgb(85, 85, 85)\">에</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;滿發</span><span style=\"color:rgb(85, 85, 85)\">한</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;雪花</span><span style=\"color:rgb(85, 85, 85)\">의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;壯觀</span><span style=\"color:rgb(85, 85, 85)\">은</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">도저히</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">얻어볼수없다</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">이럴때마다</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">나는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;「商埠國公園</span><span style=\"color:rgb(85, 85, 85)\">의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;雪花</span><span style=\"color:rgb(85, 85, 85)\">야말노</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">柳</span><span style=\"color:rgb(85, 85, 85)\">花</span><span style=\"color:rgb(85, 85, 85)\">보다</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">몇배나더</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">볼만한것을</span><span style=\"color:rgb(85, 85, 85)\">...</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">하고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">안타까운</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;歎息</span><span style=\"color:rgb(85, 85, 85)\">을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">하는것이다</span><span style=\"color:rgb(85, 85, 85)\">.</span>\r\n\r\n<div style=\"font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">海蘭江</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">강마다</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">가지고잇는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">맑고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">푸른물을가지지않고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">흐리고</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">붉은물을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">가짐만으로도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">모든강</span><span style=\"color:rgb(85, 85, 85)\">沈默</span><span style=\"color:rgb(85, 85, 85)\">을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">직히겟지</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">것츠러운</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">숨결</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">모라쉬면서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;赤海</span><span style=\"color:rgb(85, 85, 85)\">로</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">세차게</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">내리몰고잇는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">해란강의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">사나운물결도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">요새는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">어름아래서</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">고요히</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잠들어잇겟구나</span><span style=\"color:rgb(85, 85, 85)\">1</span><span style=\"color:rgb(85, 85, 85)\">그리하야</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">북국과</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">씩씩한</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">건아들아</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">적옥반이아니오가슴위를</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">용긔잇게달니면서몸과생각을함께다시겟구나</span><span style=\"color:rgb(85, 85, 85)\">?.</span><span style=\"color:rgb(85, 85, 85)\">오오</span><span style=\"color:rgb(85, 85, 85)\">-</span><span style=\"color:rgb(85, 85, 85)\">海蘭江</span><span style=\"color:rgb(85, 85, 85)\">아</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">부듸부듸그들을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">잘키워다오</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">海蘭江</span><span style=\"color:rgb(85, 85, 85)\">!</span><span style=\"color:rgb(85, 85, 85)\">너는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;來年</span><span style=\"color:rgb(85, 85, 85)\">봄</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">어름이</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">풀릴</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">때</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">첫재번</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">속삭임으로써</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">어떠한</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">약속을</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그들의</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">귀에말하여주테냐</span><span style=\"color:rgb(85, 85, 85)\">?</span><span style=\"color:rgb(85, 85, 85)\">바람</span><span style=\"color:rgb(85, 85, 85)\">?</span><span style=\"color:rgb(85, 85, 85)\">비</span><span style=\"color:rgb(85, 85, 85)\">?</span><span style=\"color:rgb(85, 85, 85)\">그도저도</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">아니면</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">빛나는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;太陽</span><span style=\"color:rgb(85, 85, 85)\">이냐</span><span style=\"color:rgb(85, 85, 85)\">?</span><span style=\"color:rgb(85, 85, 85)\">그누가알리</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">오</span><span style=\"color:rgb(85, 85, 85)\">-</span><span style=\"color:rgb(85, 85, 85)\">직</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">네홀로알고잇겟지</span><span style=\"color:rgb(85, 85, 85)\">.</span><span style=\"color:rgb(85, 85, 85)\">그러타</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">나는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">너를</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">그리워하며</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;來年</span><span style=\"color:rgb(85, 85, 85)\">봄을기다려볼테다</span><span style=\"color:rgb(85, 85, 85)\">.</span>\r\n\r\n<div style=\"font-family: 宋体; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><strong><span style=\"color:rgb(85, 85, 85)\">(</span><span style=\"color:rgb(85, 85, 85)\">乙亥&nbsp;</span><span style=\"color:rgb(85, 85, 85)\">저무러가는</span><span style=\"color:rgb(85, 85, 85)\">&nbsp;一九三五年十二月二十三日</span></strong><span style=\"color:rgb(85, 85, 85)\"><strong>)</strong></span></div>\r\n', '2017-08-30 12:22:37', '박화성', '3', '0', '0', '0', null, '0');
INSERT INTO `t_artical` VALUES ('64', '중국조선족의 미래', '<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: center;\">\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><strong><span style=\"font-size:10pt\">정판룡프로필</span></strong><strong><span style=\"font-size:10pt\">:</span></strong>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1931</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">10</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">2</span><span style=\"color:black; font-size:9pt\">일</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">전라남도</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">담양군</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">담양면</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">항교리에서</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">죽세공</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">정봉주와</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">장중대의</span><span style=\"color:black; font-size:9pt\">&nbsp;3</span><span style=\"color:black; font-size:9pt\">남으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">출생</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1938</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">중국료녕성판산현</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">영흥촌으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">이주</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1939</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">다시</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">흑룡강성</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">주하현</span><span style=\"color:black; font-size:9pt\">(</span><span style=\"color:black; font-size:9pt\">지금의</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">상지시</span><span style=\"color:black; font-size:9pt\">)</span><span style=\"color:black; font-size:9pt\">하동촌으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">이주</span><span style=\"color:black; font-size:9pt\">,</span><span style=\"color:black; font-size:9pt\">하동소학교에</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">입학</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1949</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">3</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">연변대학리공학부</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">수학부에</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">입학</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1950</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">4</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">연변대학</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">조문학부에</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">전입</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1955</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">9</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">모스크바대학</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">어문학부</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">쏘련문학강좌</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">연구생으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">입학</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1960</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">5</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">연변대학으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">돌아옴</span><span style=\"color:black; font-size:9pt\">.</span><span style=\"color:black; font-size:9pt\">어문학부</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">당총지서기</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">겸</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">부학장으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">임명받음</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1966</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">문화대혁명</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">이</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">일어나면서</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">반동학술권위</span><span style=\"color:black; font-size:9pt\">&gt;,&lt;</span><span style=\"color:black; font-size:9pt\">수정주의분자</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">몰려</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">비판투쟁을</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">받고</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">로동개조대에</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">참가등</span><span style=\"color:black; font-size:9pt\">...</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1970</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">8</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">중문련</span><span style=\"color:black; font-size:9pt\">(</span><span style=\"color:black; font-size:9pt\">중문계</span><span style=\"color:black; font-size:9pt\">)</span><span style=\"color:black; font-size:9pt\">련장</span><span style=\"color:black; font-size:9pt\">(</span><span style=\"color:black; font-size:9pt\">계주임</span><span style=\"color:black; font-size:9pt\">)</span><span style=\"color:black; font-size:9pt\">으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">임명받음</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1979</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">3</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">부교수로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">승급</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1979</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">9</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">중국작가협회에</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">가입</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1979</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">12</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">교수로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">승급</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">세계문학간사</span><span style=\"color:black; font-size:9pt\">&gt;,&lt;</span><span style=\"color:black; font-size:9pt\">조선어문수책</span><span style=\"color:black; font-size:9pt\">&gt;,&lt;</span><span style=\"color:black; font-size:9pt\">고리끼전</span><span style=\"color:black; font-size:9pt\">&gt;,&lt;</span><span style=\"color:black; font-size:9pt\">외국견문</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">등</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">출판</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1986</span><span style=\"color:black; font-size:9pt\">년부터</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">야자수</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">설레이는</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">섬나라</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">련재</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1986</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">9</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">국무원학위위원회에서</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">조선어어문학박사생</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">도사로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">임명받음</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1990</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">3</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">장편회고록</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">고향떠나</span><span style=\"color:black; font-size:9pt\">&nbsp;50</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">을</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">장백산</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">에</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">련재</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1994</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">4</span><span style=\"color:black; font-size:9pt\">월부터</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">세계속의</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">우리민족</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">이</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">연변일보에</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">련재</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1997</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">정판룡문집</span><span style=\"color:black; font-size:9pt\">&gt;(</span><span style=\"color:black; font-size:9pt\">제</span><span style=\"color:black; font-size:9pt\">2</span><span style=\"color:black; font-size:9pt\">권</span><span style=\"color:black; font-size:9pt\">)</span><span style=\"color:black; font-size:9pt\">이</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">연변인민출판사에서</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">출판</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1997</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">10</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">해외동포수상상금</span><span style=\"color:black; font-size:9pt\">&nbsp;10</span><span style=\"color:black; font-size:9pt\">만원을</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">내여</span><span style=\"color:black; font-size:9pt\">&nbsp;&lt;</span><span style=\"color:black; font-size:9pt\">정판룡교육기금회</span><span style=\"color:black; font-size:9pt\">&gt;</span><span style=\"color:black; font-size:9pt\">를</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">설립</span><span style=\"color:black; font-size:9pt\">,</span><span style=\"color:black; font-size:9pt\">제</span><span style=\"color:black; font-size:9pt\">1</span><span style=\"color:black; font-size:9pt\">차</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">장학금을</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">수여</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:black; font-size:9pt\">1994</span><span style=\"color:black; font-size:9pt\">년</span><span style=\"color:black; font-size:9pt\">4</span><span style=\"color:black; font-size:9pt\">월</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">암병으로</span><span style=\"color:black; font-size:9pt\">&nbsp;</span><span style=\"color:black; font-size:9pt\">타계</span><span style=\"color:black; font-size:9pt\">.</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><strong><span style=\"font-size:15pt\">중국조선족의</span></strong><strong><span style=\"font-size:15pt\">&nbsp;</span></strong><strong><span style=\"font-size:15pt\">미래</span></strong></p>\r\n\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;\r\n<div style=\"color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">미래</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">,</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">구체적으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">말하면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;21</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">세기의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">어떻게</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">될것인가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">?20</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">세기에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">있어서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">대부분</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">시간을</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">전쟁과</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">사회혁명에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">보내다보니</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">경제생활방면에서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">지금도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">락후한</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">상태를</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">여전히</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">벗어나지</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">못하고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">있다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">대부분은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">지금도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">여전히</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">농촌에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">거주하고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">전통적인</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">소농경형방식으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">농업생산에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">종사하고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">있으며</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">생활수준은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국국내</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">다른</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">소수민족에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">비하면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">그리</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">낮은편은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">아니나</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">국제표준으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">보면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">퍽</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">낮다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85); font-size:10pt\">본세기</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;80</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">년대부터</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국에서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">개혁개방정책을</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">실시한</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족생활에서도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">거대한</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">변화가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">일어났다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">가장</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">많이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">집거하고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">있는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">연변만</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">놓고보더라도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;1979</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">년부터</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;1991</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">년까지</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;13</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">년동안</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족개인상업자의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">수는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;72</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">배</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">,</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">자금량은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;222</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">배</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">,</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">업액은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;152</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">배가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">되였다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85); font-size:10pt\">그리고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">농촌으로부터</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">도시로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">진출하는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">증가되였으며</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">대외개방과</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">함께</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">한국</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">,</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">일본</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">등</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">국외로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">진출하는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족들도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">날따라</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">증가되는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">추세다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.1990</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">년</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">통계에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">의하면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">북경에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">사는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">수는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;6</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">천여명에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">불과했는데</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">최근</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">통계에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">의하면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">근</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;3</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">만명이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">될것이라고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하며</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">그중</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">다수는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">연변이나</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">흑룡강성</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">,</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">료녕성의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족농촌에서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">들어간</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">사람들이라는것이다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">이리하여</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">최근</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">도시인구와</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">농촌인구의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">비례는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">급격히</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">변화되고있으며</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">오라지</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">않아</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">도시인구비례가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">농촌인구비례를</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">초과할</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">전망이다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85); font-size:10pt\">다음</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">점차</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">동북아경제권이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">확립되고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">한국</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">,</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">일본</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">,</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">미국</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">등과의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">교류가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">잦아지면서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하여금</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">점차</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">선진공업민족으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">발전될</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">전망이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">뚜렷해지고있다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">특히</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">두만강하류지방이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">국제범위에서의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">경제개발의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중심지의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하나로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">되면서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">연변</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">등</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">많은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족집거구들은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">멀지않은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">장래에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">경제발전의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">속도가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">줄국에서는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">비교적</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">빠른</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">지역의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하나로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">될것으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">전망된다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">이렇게</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">되면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족들도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">늦게나마</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">점차</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">현대화한</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">선진민족을</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">행해</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">발돋음을</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하게</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">될거이며</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">이에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">상등한</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">민족문화교육의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">새로운</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">번영도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">도래할것이다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85); font-size:10pt\">그러나</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국조선족의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">미래에는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">문제점도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">적지</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">않다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">오늘까지</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">많은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">경우에는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">국가의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">힘으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">유지되던</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족문화교육사업이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">시장경제의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">도입과</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">함께</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">적지않은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">애로에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">부딛치고있으며</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">많은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족들이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">상대적으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">집중되고있는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">농촌을</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">떠나</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">도시로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">들어가면서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">민족문화보존의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">두요담보로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">되고있던</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">일부</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">민족집거구도</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">허물어져가고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">있다는것이다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">이리하여</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">일부</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">지구에서는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">민족교육이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">위기에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">처하고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">있다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85); font-size:10pt\">상대적으로</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">집거해있던</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족부락들이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">없어지거나</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족학생수가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">급속히</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">감소되면서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">어떤</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">곳에서는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">부득이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족학교를</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">없애버리거나</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">아니면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">몇개</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">학교를</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">합해야</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하며</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">또</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">그러자면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">기숙학교를</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">꾸려야</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하는데</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">나라에서나</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">사회단체에서의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">재정지원이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">없이는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">불가능하다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">그리고</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">시장경제가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">발전되면서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">돈만</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">벌면</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">되지</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">중국에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">살면서</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">우리</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">말과</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">글을</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">배워서는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">무엇하느냐</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">하는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;《</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선말무용론</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">》</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">사상이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">점차</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족들속에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">만연되기</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">시작한다는것이다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">이리하여</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">한족학교에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">들어가는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족학생수가</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">점차</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">늘어나는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">현상이다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">.</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\"><span style=\"color:rgb(85, 85, 85)\">&nbsp;</span>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"font-family: Dotum, 돋움, Tahoma, 굴림; font-size: 12px; color: rgb(34, 34, 34); margin: 0px; padding: 0px; text-align: justify;\"><span style=\"color:rgb(85, 85, 85); font-size:10pt\">다음은</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">조선족학교에</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">다니는</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">학생들의</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">학습질이</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">한족학교학생보다</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">&nbsp;</span><span style=\"color:rgb(85, 85, 85); font-size:10pt\">점차</span><span style=\"color:rgb(85, 85, 85); fon', '2017-08-30 12:24:53', '정판룡', '3', '1', '1', '0', null, '1');
